import { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";

const ForgetPassword = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");

  const submit = async () => {
    await API.post("/auth/request-reset", { email });
    navigate("/verify-code", { state: { email } });
  };

  return (
    <div className="container mt-5" style={{ maxWidth: "400px" }}>
      <div className="card card-dark p-4">
        <h4 className="brand-gradient text-center mb-3">Reset Password</h4>

        <input className="form-control mb-3" placeholder="Email"
          onChange={(e) => setEmail(e.target.value)}
        />

        <button className="btn btn-primary-custom w-100" onClick={submit}>
          Send Code
        </button>

        <button className="btn btn-link text-light mt-2" onClick={() => navigate(-1)}>
          ‚Üê Back
        </button>
      </div>
    </div>
  );
};

export default ForgetPassword;
