import { useEffect, useState } from "react";
import { FaChartLine, FaCloudRain, FaExclamationTriangle, FaInfoCircle, FaRobot } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import bg1 from "../assets/1.jpg";
import bg2 from "../assets/2.jpg";
import bg3 from "../assets/3.jpg";
import bg4 from "../assets/4.jpg";

const backgrounds = [bg1, bg2, bg3, bg4];

const Home = () => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % backgrounds.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="app-background animated-bg"
      style={{ backgroundImage: `url(${backgrounds[index]})` }}
    >
      <div className="bg-overlay"></div>

      <div className="home-page">
        {/* Hero Section */}
        <div className="home-glass-card text-center mb-4">
          <h1 className="brand-gradient fw-bold mb-2">Climate Intelligence Platform</h1>
          <p className="home-subtext mb-3">
            AI-powered rainfall & disaster prediction system for Sri Lanka
          </p>
          <button className="btn btn-green-custom px-4 py-2" onClick={() => navigate("/login")}>
            Get Started
          </button>
        </div>

        {/* Feature & Info Cards */}
        <div className="home-card-grid">
          <div className="home-feature-card">
            <FaCloudRain size={32} />
            <h5 className="card-title">Rainfall Prediction</h5>
            <p className="card-text">Accurate predictions of rainfall in real-time using AI models.</p>
          </div>

          <div className="home-feature-card">
            <FaExclamationTriangle size={32} />
            <h5 className="card-title">Disaster Alerts</h5>
            <p className="card-text">Receive immediate alerts for floods, landslides, and other disasters.</p>
          </div>

          <div className="home-feature-card">
            <FaChartLine size={32} />
            <h5 className="card-title">Data Analytics</h5>
            <p className="card-text">Visualize climate patterns and trends for informed decision-making.</p>
          </div>

          <div className="home-feature-card">
            <FaRobot size={32} />
            <h5 className="card-title">AI Forecast Models</h5>
            <p className="card-text">Advanced AI algorithms analyze weather data to improve predictions.</p>
          </div>

          <div className="home-feature-card">
            <FaInfoCircle size={32} />
            <h5 className="card-title">How It Works</h5>
            <p className="card-text">Collects satellite, sensor, and historical data to predict rainfall and disasters.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
