import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import API from "../services/api";

const ResetPassword = () => {
  const navigate = useNavigate();
  const { state } = useLocation();
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");

  const submit = async () => {
    if (password !== confirm) return alert("Passwords do not match");

    await API.post("/auth/reset-password", {
      email: state.email,
      code: state.code,
      newPassword: password
    });

    navigate("/login");
  };

  return (
    <div className="container mt-5" style={{ maxWidth: "400px" }}>
      <div className="card card-dark p-4">
        <h4 className="brand-gradient text-center mb-3">New Password</h4>

        <input type="password" className="form-control mb-3" placeholder="New Password"
          onChange={(e) => setPassword(e.target.value)}
        />

        <input type="password" className="form-control mb-3" placeholder="Confirm Password"
          onChange={(e) => setConfirm(e.target.value)}
        />

        <button className="btn btn-primary-custom w-100" onClick={submit}>
          Reset Password
        </button>

        <button className="btn btn-link text-light mt-2" onClick={() => navigate(-1)}>
          ‚Üê Back
        </button>
      </div>
    </div>
  );
};

export default ResetPassword;
