import axios from "axios";
import { useEffect, useState } from "react";
import logo from "../../assets/logo.png";
import Footer from "../../components/Footer";

// Axios instance pointing to Flask backend
const API = axios.create({
  baseURL: "http://127.0.0.1:5000",
  headers: { "Content-Type": "application/json" }
});

const ClientPredictions = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const userId = user?.userId;

  const [form, setForm] = useState({
    city: "",
    parameter: "rain_sum",
    timeframe: "week",
    min_or_max: "max"
  });

  const [alert, setAlert] = useState({ type: "", message: "" });
  const [loading, setLoading] = useState(false);
  const [predictions, setPredictions] = useState([]);

  // Alert timeout
  useEffect(() => {
    if (alert.message) {
      const timer = setTimeout(() => setAlert({ type: "", message: "" }), 3000);
      return () => clearTimeout(timer);
    }
  }, [alert]);

  // Fetch user predictions from Node backend
  const fetchPredictions = async () => {
    try {
      if (!userId) return;
      const res = await axios.get(
        `http://localhost:5001/api/predictions/user/${userId}`
      );
      setPredictions(res.data);
    } catch (err) {
      console.error(err);
      setAlert({ type: "error", message: "Failed to fetch predictions" });
    }
  };

  useEffect(() => {
    if (userId) fetchPredictions();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  // Submit prediction
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId) {
      setAlert({ type: "error", message: "User not logged in" });
      return;
    }

    setLoading(true);

    try {
      const payload = { ...form, userId };
      const res = await API.post("/predict", payload);

      console.log("Flask response:", res.data);

      setAlert({ type: "success", message: "Prediction generated!" });
      await fetchPredictions();

      setForm((prev) => ({ ...prev, city: "" }));
    } catch (err) {
      console.error(err);
      const msg = err.response?.data?.error || "Failed to generate prediction";
      setAlert({ type: "error", message: msg });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="pred-page pred-bg d-flex flex-column">
      {/* Alerts */}
      {alert.message && (
        <div
          className={`login-alert ${
            alert.type === "error" ? "login-alert-error" : "login-alert-success"
          }`}
        >
          {alert.message}
        </div>
      )}

      {/* Loading */}
      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Loading..." className="spinner-logo-large" />
        </div>
      )}

      {/* Content */}
      <div className="pred-container flex-grow-1">
        {/* Header */}
        <div className="pred-hero text-center text-white">
          <h1 className="pred-title">
            <span className="brand-gradient">Predictions</span>
          </h1>
          <p className="pred-subtitle">
            Generate a forecast and review your saved prediction history.
          </p>
        </div>

        {/* Grid */}
        <div className="pred-grid">
          {/* LEFT: Form */}
          <div className="pred-glass-card text-white">
            <h4 className="fw-bold mb-3">Generate Prediction</h4>

            <form onSubmit={handleSubmit}>
              <label className="pred-label">City</label>
              <input
                type="text"
                placeholder="e.g., Colombo"
                className="form-control glass-input mb-3"
                required
                value={form.city}
                onChange={(e) => setForm({ ...form, city: e.target.value })}
              />

              <label className="pred-label">Parameter</label>
              <select
                className="form-control glass-input mb-3"
                value={form.parameter}
                onChange={(e) => setForm({ ...form, parameter: e.target.value })}
              >
                <option value="rain_sum">Rainfall</option>
                <option value="windspeed_10m_max">Wind Speed</option>
                <option value="windgusts_10m_max">Wind Gusts</option>
              </select>

              <label className="pred-label">Timeframe</label>
              <select
                className="form-control glass-input mb-3"
                value={form.timeframe}
                onChange={(e) => setForm({ ...form, timeframe: e.target.value })}
              >
                <option value="day">Day</option>
                <option value="week">Week</option>
                <option value="month">Month</option>
              </select>

              <label className="pred-label">Mode</label>
              <select
                className="form-control glass-input mb-3"
                value={form.min_or_max}
                onChange={(e) => setForm({ ...form, min_or_max: e.target.value })}
              >
                <option value="max">Maximum</option>
                <option value="min">Minimum</option>
              </select>

              <button className="btn btn-green-custom w-100 py-2" type="submit">
                Generate Prediction
              </button>
            </form>
          </div>

          {/* RIGHT: Results */}
          <div className="pred-glass-card text-white">
            <div className="d-flex align-items-center justify-content-between mb-3">
              <h4 className="fw-bold mb-0">Prediction History</h4>
              <span className="pred-count-badge">
                {predictions.length} records
              </span>
            </div>

            {predictions.length === 0 ? (
              <p className="text-center text-white-50 mb-0">
                No predictions yet. Generate your first one!
              </p>
            ) : (
              <div className="pred-list">
                {predictions.map((p) => (
                  <div key={p._id} className="pred-item">
                    <div className="d-flex justify-content-between flex-wrap gap-2">
                      <h6 className="fw-bold mb-1">
                        {p.city} â€” {p.parameter}
                      </h6>
                      <span className="pred-risk-chip">{p.disaster_risk}</span>
                    </div>

                    <div className="pred-meta">
                      <span><strong>Timeframe:</strong> {p.timeframe}</span>
                      <span><strong>Mode:</strong> {p.min_or_max}</span>
                      <span><strong>Value:</strong> {p.predicted_value}</span>
                    </div>

                    <p className="mb-2 text-white-50">{p.forecast_message}</p>

                    {Array.isArray(p.forecast_series) && p.forecast_series.length > 0 && (
                      <div className="pred-series text-white-50">
                        <strong>Series:</strong> {p.forecast_series.join(", ")}
                      </div>
                    )}

                    {p.forecast_plot && (
                      <div className="mt-3 text-center">
                        <img
                          src={`data:image/png;base64,${p.forecast_plot}`}
                          alt="Forecast Plot"
                          className="pred-plot-img"
                        />
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ClientPredictions;
