// src/components/client/AlertsSettings.js
import axios from "axios";
import { useEffect, useMemo, useState } from "react";
import { FaBell, FaSave, FaTimes } from "react-icons/fa";
import logo from "../../assets/logo.png";

const API_BASE = "http://localhost:5001";
const API_PREFIX = "/api";
const API = axios.create({ baseURL: API_BASE });

const ALERT_TYPES = ["Tsunami", "Flood", "Storm", "Landslide"];
const SEVERITIES = ["Low", "Moderate", "High", "Critical"];

const AlertsSettings = ({ show, onClose, onSaved }) => {
  const stored = useMemo(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  }, [show]); // ✅ refresh when popup opens

  const userId = stored?.userId;

  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  const [banner, setBanner] = useState({ type: "", message: "" });

  // Form state
  const [emailAlertsEnabled, setEmailAlertsEnabled] = useState(false);
  const [alertTypes, setAlertTypes] = useState([...ALERT_TYPES]);
  const [minSeverity, setMinSeverity] = useState("High");
  const [district, setDistrict] = useState("");

  // ESC close
  useEffect(() => {
    if (!show) return;
    const onKey = (e) => e.key === "Escape" && onClose?.();
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [show, onClose]);

  // Auto-hide banner
  useEffect(() => {
    if (!banner.message) return;
    const t = setTimeout(() => setBanner({ type: "", message: "" }), 2500);
    return () => clearTimeout(t);
  }, [banner]);

  // Load preferences when popup opens
  useEffect(() => {
    const fetchPrefs = async () => {
      if (!show) return;
      if (!userId) {
        setBanner({ type: "error", message: "User not logged in" });
        return;
      }

      setLoading(true);
      try {
        const res = await API.get(`${API_PREFIX}/user/${userId}/alerts`);
        const prefs = res.data?.alertPrefs || {};

        setEmailAlertsEnabled(!!prefs.emailAlertsEnabled);

        const safeTypes = Array.isArray(prefs.alertTypes)
          ? prefs.alertTypes.filter((t) => ALERT_TYPES.includes(t))
          : [...ALERT_TYPES];

        setAlertTypes(safeTypes.length ? safeTypes : [...ALERT_TYPES]);

        setMinSeverity(
          SEVERITIES.includes(prefs.minSeverity) ? prefs.minSeverity : "High"
        );

        setDistrict(typeof prefs.district === "string" ? prefs.district : "");
      } catch (e) {
        setBanner({
          type: "error",
          message: e?.response?.data?.message || "Failed to load alert settings",
        });
      } finally {
        setLoading(false);
      }
    };

    fetchPrefs();
  }, [show, userId]);

  const toggleType = (type) => {
    setAlertTypes((prev) => {
      if (prev.includes(type)) return prev.filter((x) => x !== type);
      return [...prev, type];
    });
  };

  const selectAllTypes = () => setAlertTypes([...ALERT_TYPES]);
  const clearAllTypes = () => setAlertTypes([]);

  const handleSave = async () => {
    if (!userId) {
      setBanner({ type: "error", message: "User not logged in" });
      return;
    }

    if (emailAlertsEnabled && alertTypes.length === 0) {
      setBanner({ type: "error", message: "Select at least one alert type" });
      return;
    }

    setSaving(true);
    try {
      const payload = {
        emailAlertsEnabled,
        alertTypes,
        minSeverity,
        district,
      };

      const res = await API.put(`${API_PREFIX}/user/${userId}/alerts`, payload);

      setBanner({ type: "success", message: "Alert settings saved" });

      // ✅ update dashboard state instantly
      onSaved?.(res.data?.alertPrefs || payload);
    } catch (e) {
      setBanner({
        type: "error",
        message: e?.response?.data?.message || "Failed to save settings",
      });
    } finally {
      setSaving(false);
    }
  };

  if (!show) return null;

  return (
    <div
      className="modal d-block"
      style={{ background: "rgba(0,0,0,0.65)", zIndex: 2000 }}
      onMouseDown={(e) => {
        if (e.target === e.currentTarget) onClose?.();
      }}
    >
      <div className="modal-dialog modal-dialog-centered" style={{ maxWidth: 720 }}>
        <div className="modal-content logout-glass-card text-white" style={{ borderRadius: 18 }}>
          {/* Header */}
          <div className="modal-header border-0">
            <div className="d-flex align-items-center gap-2">
              <FaBell />
              <h5 className="modal-title fw-bold mb-0">Emergency Email Alerts</h5>
            </div>

            <button
              type="button"
              className="btn btn-outline-light"
              onClick={onClose}
              style={{ borderRadius: 12 }}
              aria-label="Close"
            >
              <FaTimes />
            </button>
          </div>

          {/* Body */}
          <div className="modal-body pt-0">
            {banner.message && (
              <div
                className={`login-alert ${
                  banner.type === "error" ? "login-alert-error" : "login-alert-success"
                }`}
                style={{ marginBottom: 14 }}
              >
                {banner.message}
              </div>
            )}

            {loading ? (
              <div className="d-flex flex-column align-items-center py-4">
                <img src={logo} alt="Loading..." style={{ height: 56 }} />
                <div className="text-white-50 mt-2">Loading settings...</div>
              </div>
            ) : (
              <>
                <p className="text-white-50 mb-3">
                  Enable alerts to receive important disaster notifications via email. You can control the alert types
                  and severity threshold.
                </p>

                {/* Toggle */}
                <div className="d-flex align-items-center justify-content-between glass-input p-3 mb-3">
                  <div>
                    <div className="fw-bold">Email Alerts</div>
                    <div className="text-white-50" style={{ fontSize: 13 }}>
                      Turn on/off emergency email alerts
                    </div>
                  </div>

                  <div className="form-check form-switch m-0">
                    <input
                      className="form-check-input"
                      type="checkbox"
                      checked={emailAlertsEnabled}
                      onChange={(e) => setEmailAlertsEnabled(e.target.checked)}
                      style={{ cursor: "pointer" }}
                    />
                  </div>
                </div>

                {/* Types */}
                <div className="glass-input p-3 mb-3">
                  <div className="d-flex align-items-center justify-content-between">
                    <div>
                      <div className="fw-bold">Alert Types</div>
                      <div className="text-white-50" style={{ fontSize: 13 }}>
                        Choose what you want to receive
                      </div>
                    </div>

                    <div className="d-flex gap-2">
                      <button
                        type="button"
                        className="btn btn-outline-light btn-sm"
                        onClick={selectAllTypes}
                        disabled={!emailAlertsEnabled}
                      >
                        Select all
                      </button>
                      <button
                        type="button"
                        className="btn btn-outline-light btn-sm"
                        onClick={clearAllTypes}
                        disabled={!emailAlertsEnabled}
                      >
                        Clear
                      </button>
                    </div>
                  </div>

                  <div className="mt-3 d-flex flex-wrap gap-3">
                    {ALERT_TYPES.map((t) => (
                      <label
                        key={t}
                        className="d-flex align-items-center gap-2"
                        style={{
                          cursor: emailAlertsEnabled ? "pointer" : "not-allowed",
                          opacity: emailAlertsEnabled ? 1 : 0.6,
                        }}
                      >
                        <input
                          type="checkbox"
                          checked={alertTypes.includes(t)}
                          onChange={() => toggleType(t)}
                          disabled={!emailAlertsEnabled}
                        />
                        <span>{t}</span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Severity + District */}
                <div className="row g-3">
                  <div className="col-md-6">
                    <label className="profile-label">Minimum Severity</label>
                    <select
                      className="form-control glass-input"
                      value={minSeverity}
                      onChange={(e) => setMinSeverity(e.target.value)}
                      disabled={!emailAlertsEnabled}
                    >
                      {SEVERITIES.map((s) => (
                        <option key={s} value={s}>
                          {s}
                        </option>
                      ))}
                    </select>
                    <div className="text-white-50 mt-1" style={{ fontSize: 12 }}>
                      Example: “High” means you won’t get Low/Moderate alerts.
                    </div>
                  </div>

                  <div className="col-md-6">
                    <label className="profile-label">District (Optional)</label>
                    <input
                      className="form-control glass-input"
                      value={district}
                      onChange={(e) => setDistrict(e.target.value)}
                      placeholder="e.g., Gampaha / Colombo"
                      disabled={!emailAlertsEnabled}
                    />
                    <div className="text-white-50 mt-1" style={{ fontSize: 12 }}>
                      Used for targeted alerts (if admin sends by district).
                    </div>
                  </div>
                </div>

                <div className="text-white-50 mt-3" style={{ fontSize: 12 }}>
                  Disclaimer: Alerts are informational and may not replace official government warnings.
                </div>
              </>
            )}
          </div>

          {/* Footer */}
          <div className="modal-footer border-0">
            <button
              type="button"
              className="btn btn-outline-light"
              onClick={onClose}
              style={{ borderRadius: 12 }}
            >
              Cancel
            </button>

            <button
              type="button"
              className="btn btn-green-custom"
              onClick={handleSave}
              disabled={loading || saving}
              style={{ borderRadius: 12, minWidth: 150 }}
            >
              {saving ? (
                "Saving..."
              ) : (
                <>
                  <FaSave className="me-2" /> Save
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AlertsSettings;
