import { useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import logo from "../assets/logo.png"; // for spinner
import API from "../services/api";

const VerifyCode = () => {
  const navigate = useNavigate();
  const { state } = useLocation();
  const [code, setCode] = useState("");
  const [alert, setAlert] = useState({ type: "", message: "" });
  const [loading, setLoading] = useState(false);
  const [timer, setTimer] = useState(180); // 3 minutes countdown

  // Redirect if accessed directly
  useEffect(() => {
    if (!state?.email) {
      navigate("/forgot-password");
    }
  }, [state, navigate]);

  // OTP countdown timer
  useEffect(() => {
    if (timer <= 0) return;
    const interval = setInterval(() => setTimer((t) => t - 1), 1000);
    return () => clearInterval(interval);
  }, [timer]);

  // Auto-hide alert after 3 seconds
  useEffect(() => {
    if (alert.message) {
      const timerId = setTimeout(() => setAlert({ type: "", message: "" }), 3000);
      return () => clearTimeout(timerId);
    }
  }, [alert]);

  const handleVerify = async () => {
    if (!code) {
      setAlert({ type: "error", message: "Please enter the verification code" });
      return;
    }

    setLoading(true);
    try {
      const res = await API.post("/auth/verify-code", {
        email: state.email,
        code,
      });
      setAlert({ type: "success", message: "Code verified successfully!" });
      setTimeout(() => navigate("/reset-password", { state: { email: state.email, code } }), 1000);
    } catch (err) {
      setAlert({
        type: "error",
        message: err.response?.data?.message || "Invalid verification code",
      });
    } finally {
      setLoading(false);
    }
  };

  // Format timer as mm:ss
  const formatTime = (t) => {
    const minutes = String(Math.floor(t / 60)).padStart(2, "0");
    const seconds = String(t % 60).padStart(2, "0");
    return `${minutes}:${seconds}`;
  };

  return (
    <div className="app-background login-background">
      {/* Logo-only Navbar */}
      <div className="navbar-wrapper position-absolute w-100">
        <div className="navbar-container d-flex align-items-center justify-content-start">
          <div className="glass-navbar brand-glass d-flex align-items-center px-3 py-2">
            <Link
              className="navbar-brand d-flex align-items-center gap-3 mb-0"
              to="/"
            >
              <img src={logo} alt="Climatrix Logo" height="52" />
              <span className="brand-gradient fw-bold fs-4">
                CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
              </span>
            </Link>
          </div>
        </div>
      </div>

      {/* Alert */}
      {alert.message && (
        <div
          className={`login-alert ${
            alert.type === "error" ? "login-alert-error" : "login-alert-success"
          }`}
        >
          {alert.message}
        </div>
      )}

      {/* Loading spinner */}
      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Loading..." className="spinner-logo-large" />
        </div>
      )}

      {!loading && (
        <div className="auth-page">
          <div className="auth-glass-card">
            {/* Back button */}
            <button
              className="btn btn-link back-button"
              onClick={() => navigate(-1)}
            >
              &#8592;
            </button>

            <h5 className="text-center text-light mb-4">Verify Code</h5>

            <form autoComplete="off">
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control glass-input"
                  placeholder="Enter OTP code"
                  required
                  autoComplete="off"
                  onChange={(e) => setCode(e.target.value)}
                />
              </div>

              <button
                type="button"
                className="btn btn-green-custom w-100 py-2"
                onClick={handleVerify}
              >
                Verify
              </button>
            </form>

            {/* Timer */}
            <div className="text-center mt-3 text-light">
              Code expires in: {formatTime(timer)}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VerifyCode;
