// pages/admin/AdminDashboard.js
import axios from "axios";
import { useEffect, useMemo, useState } from "react";
import {
  FaBell,
  FaChartBar,
  FaClipboardList,
  FaUsersCog,
} from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import { getAuthUser } from "../../utils/auth";

const API_BASE = "http://localhost:5001";
const API_PREFIX = "/api";
const API = axios.create({ baseURL: API_BASE });

const AdminDashboard = () => {
  const navigate = useNavigate();
  const user = getAuthUser();

  const stored = useMemo(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  }, []);

  const adminId = stored?.userId;

  // Optional quick stats (safe even if endpoints not ready)
  const [enabledCount, setEnabledCount] = useState(null);
  const [disabledCount, setDisabledCount] = useState(null);
  const [statsMsg, setStatsMsg] = useState("");

  // ✅ Load alert subscriber counts (optional; ignores errors if API not ready)
  useEffect(() => {
    const loadCounts = async () => {
      if (!adminId) return;

      setStatsMsg("");
      try {
        const [enabledRes, disabledRes] = await Promise.all([
          API.get(`${API_PREFIX}/users/alerts?enabled=true`),
          API.get(`${API_PREFIX}/users/alerts?enabled=false`),
        ]);

        // backend returns list of users
        setEnabledCount(Array.isArray(enabledRes.data) ? enabledRes.data.length : null);
        setDisabledCount(Array.isArray(disabledRes.data) ? disabledRes.data.length : null);
      } catch {
        // keep it silent but show small hint
        setStatsMsg("Some admin stats are not available yet.");
      }
    };

    loadCounts();
  }, [adminId]);

  return (
    <div className="client-dashboard-page dashboard-bg">
      {/* Welcome Section */}
      <div className="dashboard-hero text-center">
        <h1 className="dashboard-title">
          <span className="brand-gradient">Admin Panel,</span> {user?.name}.
        </h1>

        <p className="dashboard-subtitle">
          Manage users, control alert systems, review prediction analytics, and handle questions.
        </p>

        {(enabledCount !== null || disabledCount !== null || statsMsg) && (
          <div className="text-white-50 mt-2" style={{ fontSize: "0.95rem" }}>
            {enabledCount !== null && (
              <span className="me-3">Alerts Enabled: <b>{enabledCount}</b></span>
            )}
            {disabledCount !== null && (
              <span className="me-3">Alerts Disabled: <b>{disabledCount}</b></span>
            )}
            {statsMsg && <span>{statsMsg}</span>}
          </div>
        )}
      </div>

      {/* Cards Grid */}
      <div className="dashboard-cards-grid">
        {/* Card 1: User Management */}
        <div className="dashboard-feature-card text-white">
          <div className="dashboard-card-top">
            <div className="dashboard-card-icon">
              <FaUsersCog />
            </div>
            <span className="dashboard-card-badge">Admin</span>
          </div>

          <h5 className="fw-bold mt-3">User Management</h5>
          <p className="dashboard-card-text">
            View users, search, filter by role, and manage account access and status.
          </p>

          <button
            className="btn btn-green-custom w-100 py-2 mt-2"
            onClick={() => navigate("/admin/users")}
          >
            Manage Users
          </button>
        </div>

        {/* Card 2: Alert System Management */}
        <div className="dashboard-feature-card text-white">
          <div className="dashboard-card-top">
            <div className="dashboard-card-icon">
              <FaBell />
            </div>
            <span className="dashboard-card-badge">Safety</span>
          </div>

          <h5 className="fw-bold mt-3">Alert System Management</h5>
          <p className="dashboard-card-text">
            View subscribers and prepare emergency alert emails to send to enabled users.
          </p>

          <button
            className="btn btn-outline-light w-100 py-2 mt-2"
            onClick={() => navigate("/admin/alerts")}
          >
            Open Alert System
          </button>
        </div>

        {/* Card 3: Prediction History & Analysis */}
        <div className="dashboard-feature-card text-white">
          <div className="dashboard-card-top">
            <div className="dashboard-card-icon">
              <FaChartBar />
            </div>
            <span className="dashboard-card-badge">Analytics</span>
          </div>

          <h5 className="fw-bold mt-3">Prediction History & Analyze</h5>
          <p className="dashboard-card-text">
            Review system-wide prediction history, track trends, and analyze model outcomes.
          </p>

          <button
            className="btn btn-outline-light w-100 py-2 mt-2"
            onClick={() => navigate("/admin/prediction-history")}
          >
            View & Analyze
          </button>
        </div>

        {/* Card 4: Question Management */}
        <div className="dashboard-feature-card text-white">
          <div className="dashboard-card-top">
            <div className="dashboard-card-icon">
              <FaClipboardList />
            </div>
            <span className="dashboard-card-badge">Support</span>
          </div>

          <h5 className="fw-bold mt-3">Question Management</h5>
          <p className="dashboard-card-text">
            Review user questions, respond, update status, and maintain support quality.
          </p>

          <button
            className="btn btn-outline-light w-100 py-2 mt-2"
            onClick={() => navigate("/admin/questions")}
          >
            Manage Questions
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
