import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo.png"; // spinner & navbar logo
import API from "../services/api";

const ForgetPassword = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [alert, setAlert] = useState({ type: "", message: "" });
  const [loading, setLoading] = useState(false);

  // Auto-hide alert after 3 seconds
  useEffect(() => {
    if (alert.message) {
      const timer = setTimeout(() => setAlert({ type: "", message: "" }), 3000);
      return () => clearTimeout(timer);
    }
  }, [alert]);

  const submit = async () => {
    // Basic validation
    if (!email.includes("@")) {
      setAlert({ type: "error", message: "Please enter a valid email address" });
      return;
    }

    setLoading(true);
    try {
      await API.post("/auth/request-reset", { email });
      setAlert({ type: "success", message: "Verification code sent!" });
      setTimeout(() => navigate("/verify-code", { state: { email } }), 1000);
    } catch (err) {
      setAlert({
        type: "error",
        message: err.response?.data?.message || "Failed to send code",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-background login-background">
      {/* Logo-only Navbar */}
      <div className="navbar-wrapper position-absolute w-100">
        <div className="navbar-container d-flex align-items-center justify-content-start">
          <div className="glass-navbar brand-glass d-flex align-items-center px-3 py-2">
            <Link
              className="navbar-brand d-flex align-items-center gap-3 mb-0"
              to="/"
            >
              <img src={logo} alt="Climatrix Logo" height="52" />
              <span className="brand-gradient fw-bold fs-4">
                CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
              </span>
            </Link>
          </div>
        </div>
      </div>

      {/* Alert message */}
      {alert.message && (
        <div
          className={`login-alert ${
            alert.type === "error" ? "login-alert-error" : "login-alert-success"
          }`}
        >
          {alert.message}
        </div>
      )}

      {/* Loading spinner */}
      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Loading..." className="spinner-logo-large" />
        </div>
      )}

      {!loading && (
        <div className="auth-page">
          <div className="auth-glass-card">
            {/* Back button */}
            <button
              className="btn btn-link back-button"
              onClick={() => navigate(-1)}
            >
              &#8592;
            </button>

            <h5 className="text-center text-light mb-4">Reset Password</h5>

            <form autoComplete="off">
              <div className="mb-4">
                <input
                  type="email"
                  className="form-control glass-input"
                  placeholder="Email Address"
                  required
                  autoComplete="off"
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>

              <button
                className="btn btn-green-custom w-100 py-2"
                type="button"
                onClick={submit}
              >
                Send Code
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ForgetPassword;
