import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import bg1 from "../assets/1.jpg";
import bg2 from "../assets/home-bg.jpg";
import bg3 from "../assets/nav-bg.jpg";

const backgrounds = [bg1, bg2, bg3];

const Home = () => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % backgrounds.length);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="app-background animated-bg"
      style={{ backgroundImage: `url(${backgrounds[index]})` }}
    >
      <div className="bg-overlay"></div>

      <div className="home-page">
        <div className="home-glass-card text-center">
          <h1 className="brand-gradient fw-bold mb-3">
            Climate Intelligence Platform
          </h1>

          <p className="home-subtext mb-4">
            Secure access to Climatrix Sri Lanka services
          </p>

          <button
            className="btn btn-green-custom px-4 py-2"
            onClick={() => navigate("/login")}
          >
            Get Started
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
