// pages/client/Profile.js
import axios from "axios";
import { useEffect, useMemo, useRef, useState } from "react";
import { FaCamera, FaLock, FaSave, FaTimes, FaUser } from "react-icons/fa";
import logo from "../../assets/logo.png";
import Footer from "../../components/Footer";

// ✅ Node backend base
const API_BASE = "http://localhost:5001";

// Axios instance
const API = axios.create({
  baseURL: API_BASE,
});

const formatDateTime = (iso) => {
  if (!iso) return "—";
  const d = new Date(iso);
  if (Number.isNaN(d.getTime())) return "—";
  return d.toLocaleString();
};
const Profile = () => {
  const stored = useMemo(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  }, []);

  const userId = stored?.userId;

  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ type: "", message: "" });

  // profile (from API)
  const [profile, setProfile] = useState(null);

  // editable fields
  const [name, setName] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(""); // image preview

  // password change
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const fileRef = useRef(null);

  // Auto-hide alerts
  useEffect(() => {
    if (!alert.message) return;
    const t = setTimeout(() => setAlert({ type: "", message: "" }), 3000);
    return () => clearTimeout(t);
  }, [alert]);

  // Fetch profile
  const fetchProfile = async () => {
    if (!userId) {
      setAlert({ type: "error", message: "User not logged in" });
      return;
    }

    setLoading(true);
    try {
      const res = await API.get(`/api/user/user/${userId}`);
      const u = res.data;

      setProfile(u);
      setName(u?.name || "");
      setPreviewUrl(u?.profilePicture || ""); // backend already returns full URL
    } catch (e) {
      console.error(e);
      setAlert({ type: "error", message: "Failed to load profile" });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  // Choose new image
  const onPickImage = (file) => {
    if (!file) return;

    // Basic validation
    const okTypes = ["image/jpeg", "image/png", "image/webp", "image/jpg"];
    if (!okTypes.includes(file.type)) {
      setAlert({ type: "error", message: "Please upload a JPG/PNG/WEBP image" });
      return;
    }
    if (file.size > 3 * 1024 * 1024) {
      setAlert({ type: "error", message: "Image size should be under 3MB" });
      return;
    }

    setImageFile(file);

    // Preview
    const url = URL.createObjectURL(file);
    setPreviewUrl(url);
  };

  const resetChanges = () => {
    if (!profile) return;
    setName(profile.name || "");
    setImageFile(null);
    setPreviewUrl(profile.profilePicture || "");
    setCurrentPassword("");
    setNewPassword("");
    setConfirmPassword("");
    setAlert({ type: "", message: "" });
  };

  // Update localStorage user + dispatch event (for navbar/sidebar avatar updates)
  const updateLocalStorageUser = (updates) => {
    try {
      const existing = JSON.parse(localStorage.getItem("user")) || {};
      const merged = { ...existing, ...updates };
      localStorage.setItem("user", JSON.stringify(merged));

      // ✅ Let Navbar/VerticalBar react if they listen
      window.dispatchEvent(new Event("userUpdated"));
    } catch (e) {
      console.warn("Failed to update localStorage user", e);
    }
  };

  // Submit update
  const handleSave = async (e) => {
    e.preventDefault();

    if (!userId) {
      setAlert({ type: "error", message: "User not logged in" });
      return;
    }

    // Client-side checks
    if (!name.trim()) {
      setAlert({ type: "error", message: "Name is required" });
      return;
    }

    const wantsPasswordChange = currentPassword || newPassword || confirmPassword;
    if (wantsPasswordChange) {
      if (!currentPassword || !newPassword || !confirmPassword) {
        setAlert({ type: "error", message: "Fill all password fields to change password" });
        return;
      }
      if (newPassword.length < 6) {
        setAlert({ type: "error", message: "New password should be at least 6 characters" });
        return;
      }
      if (newPassword !== confirmPassword) {
        setAlert({ type: "error", message: "New password and confirm password do not match" });
        return;
      }
    }

    setLoading(true);

    try {
      // ✅ IMPORTANT: backend expects field name 'image' (routes: upload.single('image'))
      const formData = new FormData();
      formData.append("name", name.trim());

      // Send password fields exactly like controller expects
      if (wantsPasswordChange) {
        formData.append("currentPassword", currentPassword);
        formData.append("newPassword", newPassword);
        formData.append("confirmPassword", confirmPassword);
      }

      if (imageFile) {
        formData.append("image", imageFile);
      }

      const res = await API.put(`/api/user/user/${userId}/profile`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // Expected response:
      // { message, user: { name, email, profilePicture } }
      const updatedUser = res.data?.user;

      setAlert({ type: "success", message: "Profile updated successfully" });

      // Refresh UI states
      setProfile((prev) => ({
        ...(prev || {}),
        name: updatedUser?.name ?? name.trim(),
        // email unchanged
        profilePicture: updatedUser?.profilePicture ?? prev?.profilePicture,
      }));

      // Update preview URL from server (important after upload)
      if (updatedUser?.profilePicture) {
        setPreviewUrl(updatedUser.profilePicture);
      }

      // Clear password fields and imageFile (but keep preview)
      setImageFile(null);
      setCurrentPassword("");
      setNewPassword("");
      setConfirmPassword("");

      // ✅ Update local storage (so navbar/profile container updates)
      updateLocalStorageUser({
        name: updatedUser?.name ?? name.trim(),
        profilePicture: updatedUser?.profilePicture ?? stored?.profilePicture,
      });
    } catch (err) {
      console.error(err);
      const msg =
        err.response?.data?.message ||
        err.response?.data?.error ||
        "Failed to update profile";
      setAlert({ type: "error", message: msg });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-page d-flex flex-column">
      {/* Alerts */}
      {alert.message && (
        <div className={`login-alert ${alert.type === "error" ? "login-alert-error" : "login-alert-success"}`}>
          {alert.message}
        </div>
      )}

      {/* Loading */}
      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Loading..." className="spinner-logo-large" />
        </div>
      )}

      <div className="profile-container flex-grow-1">
        {/* Header */}
        <div className="profile-hero">
          <h1 className="profile-title">
            <span className="brand-gradient">My Profile</span>
          </h1>
          <div className="text-white-50">Update your name, profile picture, and password.</div>
        </div>

        <div className="profile-grid">
          {/* Left card (avatar + summary) */}
          <div className="profile-glass-card">
            <div className="profile-avatar-wrap">
              <div className="profile-avatar-ring">
                <img
                  src={previewUrl || "https://via.placeholder.com/160?text=User"}
                  alt="Profile"
                  className="profile-avatar"
                />
              </div>

              <button
                type="button"
                className="profile-avatar-btn"
                onClick={() => fileRef.current?.click()}
                title="Change profile picture"
              >
                <FaCamera />
              </button>

              <input
                ref={fileRef}
                type="file"
                accept="image/*"
                style={{ display: "none" }}
                onChange={(e) => onPickImage(e.target.files?.[0])}
              />
            </div>

            <div className="profile-summary">
              <div className="profile-name">{profile?.name || "—"}</div>
              <div className="profile-role-chip">{profile?.role || "Client"}</div>

              <div className="profile-kv">
                <div className="profile-k">Email</div>
                <div className="profile-v">{profile?.email || "—"}</div>
              </div>

              <div className="profile-kv">
                <div className="profile-k">User ID</div>
                <div className="profile-v">{profile?.userId ?? "—"}</div>
              </div>

              <div className="profile-kv">
                <div className="profile-k">Joined</div>
                <div className="profile-v">{formatDateTime(profile?.createdAt)}</div>
              </div>
            </div>
          </div>

          {/* Right card (edit form) */}
          <div className="profile-glass-card">
            <form onSubmit={handleSave}>
              <div className="profile-section-title">
                <FaUser className="me-2" /> Profile Details
              </div>

              {/* Editable name */}
              <label className="profile-label">Name</label>
              <input
                className="form-control glass-input mb-3"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Your name"
                required
              />

              {/* Non-editable details */}
              <div className="profile-readonly-grid">
                <div>
                  <label className="profile-label">Email</label>
                  <input className="form-control glass-input" value={profile?.email || ""} disabled />
                </div>
                <div>
                  <label className="profile-label">User ID</label>
                  <input className="form-control glass-input" value={profile?.userId ?? ""} disabled />
                </div>
              </div>

              <div className="profile-section-title mt-4">
                <FaLock className="me-2" /> Change Password
              </div>

              <div className="profile-password-grid">
                <div>
                  <label className="profile-label">Current Password</label>
                  <input
                    type="password"
                    className="form-control glass-input"
                    value={currentPassword}
                    onChange={(e) => setCurrentPassword(e.target.value)}
                    placeholder="Enter current password"
                  />
                </div>

                <div>
                  <label className="profile-label">New Password</label>
                  <input
                    type="password"
                    className="form-control glass-input"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    placeholder="Enter new password"
                  />
                </div>

                <div>
                  <label className="profile-label">Confirm Password</label>
                  <input
                    type="password"
                    className="form-control glass-input"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    placeholder="Confirm new password"
                  />
                </div>
              </div>

              <div className="profile-actions">
                <button type="button" className="btn btn-outline-light profile-btn" onClick={resetChanges}>
                  <FaTimes className="me-2" />
                  Reset
                </button>

                <button type="submit" className="btn btn-green-custom profile-btn">
                  <FaSave className="me-2" />
                  Save Changes
                </button>
              </div>

              <div className="profile-hint text-white-50">
                Tip: If you don’t want to change password, keep password fields empty.
              </div>
            </form>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Profile;
