import { FaChartLine, FaHistory, FaSignOutAlt, FaTachometerAlt } from "react-icons/fa";
import { NavLink, useNavigate } from "react-router-dom";
import logo from "../../assets/logo.png";

const ClientSidebar = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user")); // logged-in user

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="d-flex flex-column vh-100 p-3 bg-glass client-sidebar">

      {/* ðŸ”¹ Logo */}
      <div className="text-center mb-4">
        <div className="glass-navbar brand-glass p-2 d-flex flex-column align-items-center">
          <img src={logo} alt="Climatrix Logo" height="52" />
          <span className="brand-gradient fw-bold fs-4 mt-2">
            CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
          </span>
        </div>
      </div>

      {/* ðŸ”¹ Profile Container */}
      <div className="glass-navbar profile-glass d-flex flex-column align-items-center p-3 mb-5">
        <img
          src={user?.profilePicture || "/default-avatar.png"}
          alt="Profile"
          className="rounded-circle mb-2"
          style={{ width: "80px", height: "80px", objectFit: "cover" }}
        />
        <strong>{user?.name}</strong>
        <small className="text-muted">{user?.email}</small>
      </div>

      {/* ðŸ”¹ Navigation Links */}
      <nav className="d-flex flex-column gap-2">
        <NavLink
          to="/client/dashboard"
          className={({ isActive }) =>
            `glass-navbar nav-glass px-3 py-2 d-flex align-items-center gap-2 ${
              isActive ? "active-nav" : ""
            }`
          }
        >
          <FaTachometerAlt /> Dashboard
        </NavLink>

        <NavLink
          to="/client/predictions"
          className={({ isActive }) =>
            `glass-navbar nav-glass px-3 py-2 d-flex align-items-center gap-2 ${
              isActive ? "active-nav" : ""
            }`
          }
        >
          <FaChartLine /> Predictions
        </NavLink>

        <NavLink
          to="/client/prediction-history"
          className={({ isActive }) =>
            `glass-navbar nav-glass px-3 py-2 d-flex align-items-center gap-2 ${
              isActive ? "active-nav" : ""
            }`
          }
        >
          <FaHistory /> History
        </NavLink>

        <button
          className="btn btn-danger mt-auto w-100 d-flex align-items-center justify-content-center gap-2"
          onClick={handleLogout}
        >
          <FaSignOutAlt /> Logout
        </button>
      </nav>
    </div>
  );
};

export default ClientSidebar;
