import { FaChartLine, FaHistory, FaSignOutAlt, FaTachometerAlt } from "react-icons/fa";
import { NavLink, useNavigate } from "react-router-dom";
import logo from "../../assets/logo.png";

const ClientSidebar = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user")); // logged-in user

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="client-sidebar d-flex flex-column align-items-center p-3 vh-100 bg-glass">

      {/* ðŸ”¹ Logo */}
      <div className="sidebar-logo mb-4 text-center">
        <img src={logo} alt="Climatrix Logo" height="50" />
        <h5 className="brand-gradient mt-2">
          CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
        </h5>
      </div>

      {/* ðŸ”¹ Profile Container */}
      <div className="profile-container d-flex flex-column align-items-center mb-5 p-2 text-center">
        <img
          src={user?.profilePicture || "/default-avatar.png"}
          alt="Profile"
          className="rounded-circle mb-2"
          style={{ width: "80px", height: "80px", objectFit: "cover" }}
        />
        <strong>{user?.name}</strong>
        <small className="text-muted">{user?.email}</small>
      </div>

      {/* ðŸ”¹ Navigation Links */}
      <nav className="d-flex flex-column gap-3 w-100">
        <NavLink
          to="/client/dashboard"
          className={({ isActive }) =>
            `sidebar-link d-flex align-items-center gap-2 px-3 py-2 ${
              isActive ? "active-link" : ""
            }`
          }
        >
          <FaTachometerAlt /> Dashboard
        </NavLink>

        <NavLink
          to="/client/predictions"
          className={({ isActive }) =>
            `sidebar-link d-flex align-items-center gap-2 px-3 py-2 ${
              isActive ? "active-link" : ""
            }`
          }
        >
          <FaChartLine /> Predictions
        </NavLink>

        <NavLink
          to="/client/prediction-history"
          className={({ isActive }) =>
            `sidebar-link d-flex align-items-center gap-2 px-3 py-2 ${
              isActive ? "active-link" : ""
            }`
          }
        >
          <FaHistory /> History
        </NavLink>

        <button
          className="btn btn-danger mt-auto w-100 d-flex align-items-center justify-content-center gap-2"
          onClick={handleLogout}
        >
          <FaSignOutAlt /> Logout
        </button>
      </nav>
    </div>
  );
};

export default ClientSidebar;
