// App.js
import { BrowserRouter, Route, Routes, useLocation } from "react-router-dom";
import Footer from "./components/Footer";
import Navbar from "./components/Navbar";

// Public Pages
import About from "./pages/About";
import ForgetPassword from "./pages/ForgetPassword";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import ResetPassword from "./pages/ResetPassword";
import VerifyCode from "./pages/VerifyCode";

// Admin Pages
import AdminDashboard from "./pages/admin/AdminDashboard";
import UserManagement from "./pages/admin/UserManagement";

// Client Pages
import ClientDashboard from "./pages/client/ClientDashboard";
import ClientInsights from "./pages/client/Insights";
import PredictionHistory from "./pages/client/PredictionHistory";
import ClientPredictions from "./pages/client/Predictions";
import ClientProfile from "./pages/client/Profile";

// Sidebar Components
import ClientSidebar from "./pages/client/ClientSidebar";
import VerticalBar from "./pages/client/VerticalBar";

// Route Guard
import ProtectedRoute from "./routes/ProtectedRoute";

import "bootstrap/dist/css/bootstrap.min.css";
import "./styles/theme.css";

/* ✅ Client Layout (Sidebar only) */
const ClientLayout = ({ children }) => {
  return (
    <div className="d-flex vh-100">
      <ClientSidebar />
      <div className="flex-grow-1 overflow-auto p-3 client-layout-with-sidebar">
        {children}
      </div>
    </div>
  );
};

/* ✅ Client Layout (Sidebar + VerticalBar) */
const ClientLayoutWithVerticalBar = ({ children }) => {
  return (
    <div className="d-flex vh-100">
      <ClientSidebar />
      <VerticalBar />
      <div className="flex-grow-1 overflow-auto p-3 client-layout-with-sidebar">
        {children}
      </div>
    </div>
  );
};

/* ✅ Admin Layout (ClientSidebar reused + scroll enabled) */
const AdminLayout = ({ children }) => {
  return (
    <div className="d-flex vh-100">
      {/* ✅ Reuse same sidebar UI */}
      <ClientSidebar />

      {/* ✅ Same padding so content doesn't hide behind sidebar */}
      <div className="flex-grow-1 overflow-auto p-3 client-layout-with-sidebar">
        {children}
      </div>
    </div>
  );
};

function AppLayout() {
  const location = useLocation();

  const hideFooterOnHome = location.pathname === "/";
  const isClientRoute = location.pathname.startsWith("/client");
  const isAdminRoute = location.pathname.startsWith("/admin");

  return (
    <>
      {/* ✅ Only show Navbar on public pages */}
      {!isClientRoute && !isAdminRoute && <Navbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<ForgetPassword />} />
        <Route path="/verify-code" element={<VerifyCode />} />
        <Route path="/reset-password" element={<ResetPassword />} />
        <Route path="/about" element={<About />} />

        {/* Admin Routes */}
        <Route
          path="/admin/dashboard"
          element={
            <ProtectedRoute allowedRoles={["Admin"]}>
              <AdminLayout>
                <AdminDashboard />
              </AdminLayout>
            </ProtectedRoute>
          }
        />

        {/* Client Routes */}
        <Route
          path="/client/dashboard"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayout>
                <ClientDashboard />
              </ClientLayout>
            </ProtectedRoute>
          }
        />

        <Route
          path="/client/predictions"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <ClientPredictions />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />

        <Route
          path="/client/prediction-history"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <PredictionHistory />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />

        <Route
          path="/client/insights"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <ClientInsights />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />

        <Route
          path="/client/profile"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <ClientProfile />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />
      </Routes>
      <Route
  path="/admin/users"
  element={
    <ProtectedRoute allowedRoles={["Admin"]}>
      <AdminLayout>
        <UserManagement />
      </AdminLayout>
    </ProtectedRoute>
  }
/>

      {/* Footer only on public pages */}
      {!hideFooterOnHome && !isClientRoute && !isAdminRoute && <Footer />}
    </>
  );
}

function App() {
  return (
    <BrowserRouter>
      <AppLayout />
    </BrowserRouter>
  );
}

export default App;
