import { useEffect, useState } from "react";
import API from "..";
import Footer from "../components/Footer";
import logo from "../assets/logo.png";

const ClientPredictions = () => {
  const user = JSON.parse(localStorage.getItem("user")); // get logged-in user
  const userId = user?.userId;

  const [form, setForm] = useState({
    city: "",
    parameter: "rain_sum",
    timeframe: "week",
    min_or_max: "max"
  });
  const [alert, setAlert] = useState({ type: "", message: "" });
  const [loading, setLoading] = useState(false);
  const [predictions, setPredictions] = useState([]);

  // Alert timeout
  useEffect(() => {
    if (alert.message) {
      const timer = setTimeout(() => setAlert({ type: "", message: "" }), 3000);
      return () => clearTimeout(timer);
    }
  }, [alert]);

  // Fetch user predictions
  const fetchPredictions = async () => {
    try {
      const res = await API.get(`/predictions/user/${userId}`);
      setPredictions(res.data);
    } catch (err) {
      console.error(err);
      setAlert({ type: "error", message: "Failed to fetch predictions" });
    }
  };

  useEffect(() => {
    if (userId) fetchPredictions();
  }, [userId]);

  // Submit prediction
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!userId) {
      setAlert({ type: "error", message: "User not logged in" });
      return;
    }

    setLoading(true);
    try {
      const payload = { ...form, userId }; // send userId to Flask
      const res = await API.post("/predict", payload);

      setAlert({ type: "success", message: "Prediction generated!" });
      fetchPredictions(); // refresh list
    } catch (err) {
      console.error(err);
      setAlert({ type: "error", message: "Failed to generate prediction" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-background login-background d-flex flex-column">
      {alert.message && (
        <div
          className={`login-alert ${
            alert.type === "error" ? "login-alert-error" : "login-alert-success"
          }`}
        >
          {alert.message}
        </div>
      )}

      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Loading..." className="spinner-logo-large" />
        </div>
      )}

      <div className="auth-page flex-grow-1">
        <div className="auth-glass-card">
          <h3 className="text-center brand-gradient mb-4">Your Predictions</h3>

          {/* Prediction Form */}
          <form onSubmit={handleSubmit} className="mb-4">
            <input
              type="text"
              placeholder="City"
              className="form-control glass-input mb-2"
              required
              value={form.city}
              onChange={(e) => setForm({ ...form, city: e.target.value })}
            />

            <select
              className="form-control glass-input mb-2"
              value={form.parameter}
              onChange={(e) => setForm({ ...form, parameter: e.target.value })}
            >
              <option value="rain_sum">Rainfall</option>
              <option value="windspeed_10m_max">Wind Speed</option>
              <option value="windgusts_10m_max">Wind Gusts</option>
            </select>

            <select
              className="form-control glass-input mb-2"
              value={form.timeframe}
              onChange={(e) => setForm({ ...form, timeframe: e.target.value })}
            >
              <option value="day">Day</option>
              <option value="week">Week</option>
              <option value="month">Month</option>
            </select>

            <select
              className="form-control glass-input mb-2"
              value={form.min_or_max}
              onChange={(e) => setForm({ ...form, min_or_max: e.target.value })}
            >
              <option value="max">Maximum</option>
              <option value="min">Minimum</option>
            </select>

            <button className="btn btn-green-custom w-100 py-2" type="submit">
              Generate Prediction
            </button>
          </form>

          {/* Prediction List */}
          <div>
            {predictions.length === 0 && (
              <p className="text-center">No predictions yet.</p>
            )}

            {predictions.map((p) => (
              <div key={p._id} className="prediction-card mb-3 p-2">
                <h5>{p.city} - {p.parameter}</h5>
                <p>
                  <strong>Timeframe:</strong> {p.timeframe} |{" "}
                  <strong>Min/Max:</strong> {p.min_or_max} |{" "}
                  <strong>Value:</strong> {p.predicted_value} |{" "}
                  <strong>Risk:</strong> {p.disaster_risk}
                </p>
                <p>{p.forecast_message}</p>
                <div>
                  <strong>Series:</strong> {p.forecast_series.join(", ")}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ClientPredictions;
