import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import bg1 from "../assets/1.jpg";
import bg2 from "../assets/2.jpg";
import bg3 from "../assets/3.jpg";
import bg4 from "../assets/4.jpg";

const backgrounds = [bg1, bg2, bg3, bg4];

const Home = () => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % backgrounds.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="app-background animated-bg"
      style={{ backgroundImage: `url(${backgrounds[index]})` }}
    >
      <div className="bg-overlay"></div>

      <div className="home-layout">

        {/* ===== Main Content ===== */}
        <div className="home-main">
          <h1 className="home-title">
            Predict. <span className="brand-gradient">Prepare.</span> Protect.
          </h1>
          <p className="home-description">
            AI-powered climate intelligence platform for rainfall prediction and
            disaster preparedness in Sri Lanka.
          </p>
        </div>

        {/* ===== Glass Cards ===== */}
        <div className="home-cards">
          <div className="info-glass-card">
            <h5>AI Rainfall Prediction</h5>
            <p>
              Machine learning models analyze historical and real-time climate
              data to forecast rainfall accurately.
            </p>
          </div>

          <div className="info-glass-card">
            <h5>Disaster Risk Alerts</h5>
            <p>
              Early warnings for floods and climate-related risks to support
              timely decision-making.
            </p>
          </div>

          <div className="info-glass-card">
            <h5>Climate Insights</h5>
            <p>
              Visualized climate trends and predictive analytics for authorities,
              researchers, and communities.
            </p>
          </div>
        </div>

        {/* ===== Bottom Buttons ===== */}
        <div className="home-actions">
          <button
            className="btn glass-action-btn"
            onClick={() => navigate("/login")}
          >
            Predictions
          </button>

          <button
            className="btn glass-action-btn"
            onClick={() => navigate("/login")}
          >
            Login
          </button>

          <button
            className="btn glass-action-btn"
            onClick={() => navigate("/register")}
          >
            Register
          </button>
        </div>

      </div>
    </div>
  );
};

export default Home;
