import { useNavigate } from "react-router-dom";
import { getAuthUser } from "../../utils/auth";

const ClientDashboard = () => {
  const navigate = useNavigate();
  const user = getAuthUser();

  return (
    <div className="client-dashboard-root">
      <div className="dashboard-glass-card text-white">

        <h2 className="fw-bold mb-3">
          Welcome, <span className="text-success">{user?.name}</span>
        </h2>

        <p className="dashboard-subtext">
          Access your climate prediction tools and manage your prediction history.
        </p>

        <div className="dashboard-actions mt-4">
          <button
            className="btn btn-green-custom px-4 py-2"
            onClick={() => navigate("/client/predictions")}
          >
            Make Prediction
          </button>

          <button
            className="btn btn-outline-light px-4 py-2"
            onClick={() => navigate("/client/prediction-history")}
          >
            View Prediction History
          </button>
        </div>

      </div>
    </div>
  );
};

export default ClientDashboard;
