import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo.png"; // logo for spinner
import API from "../services/api";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const res = await API.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      navigate("/");
    } catch (err) {
      setError(err.response?.data?.message || "Login failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-background login-background">
      {/* Top alert message */}
      {error && (
        <div className="glass-alert glass-alert-danger login-alert">
          {error}
        </div>
      )}

      {/* Back button under navbar */}
      <button
        className="btn btn-link back-button"
        onClick={() => navigate(-1)}
      >
        &#8592;
      </button>

      <div className="auth-page">
        <div className="auth-glass-card">

          <h3 className="text-center brand-gradient mb-4">
            Welcome Back
          </h3>

          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <input
                type="email"
                className="form-control glass-input"
                placeholder="Email address"
                required
                onChange={(e) =>
                  setForm({ ...form, email: e.target.value })
                }
              />
            </div>

            <div className="mb-4">
              <input
                type="password"
                className="form-control glass-input"
                placeholder="Password"
                required
                onChange={(e) =>
                  setForm({ ...form, password: e.target.value })
                }
              />
            </div>

            <button className="btn btn-green-custom w-100 py-2" type="submit" disabled={loading}>
              {loading ? (
                <img src={logo} alt="Loading..." className="spinner-logo" />
              ) : (
                "Login"
              )}
            </button>
          </form>

          <div className="text-center mt-3 signup-text">
            Do not have an account? <Link className="link-green" to="/register">Sign Up</Link>
          </div>

          <div className="text-center mt-3">
            <Link className="link-green" to="/forgot-password">
              Forgot password?
            </Link>
          </div>

        </div>
      </div>
    </div>
  );
};

export default Login;
