import { FaSignOutAlt } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import logo from "../../assets/logo.png";

const ClientNavbar = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user")); // logged-in user

  const backendURL = "http://localhost:5001/"; // Node backend URL

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="navbar-wrapper position-absolute w-100">
      <div className="navbar-container d-flex align-items-center justify-content-between">

        {/* ðŸ”¹ Glass Bar â€“ Logo */}
        <div className="glass-navbar brand-glass d-flex align-items-center">
          <img src={logo} alt="Climatrix Logo" height="52" />
          <span className="brand-gradient fw-bold fs-4 ms-2">
            CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
          </span>
        </div>

        {/* ðŸ”¹ Glass Bar â€“ Profile Container */}
        <div className="glass-navbar nav-glass d-flex align-items-center gap-2 px-3 py-1 rounded">
          <img
            src={user?.profilePicture ? `${backendURL}${user.profilePicture}` : "/default-avatar.png"}
            alt="Profile"
            className="rounded-circle"
            style={{ width: "50px", height: "50px", objectFit: "cover" }}
          />
          <div>
            <div className="fw-bold">{user?.name}</div>
            <div className="text-muted" style={{ fontSize: "0.85rem" }}>
              {user?.email}
            </div>
          </div>
          <button
            className="btn btn-danger btn-sm ms-3"
            onClick={handleLogout}
          >
            <FaSignOutAlt /> Logout
          </button>
        </div>

      </div>
    </div>
  );
};

export default ClientNavbar;
