import { FaSignOutAlt } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import logo from "../../assets/logo.png";

const ClientNavbar = () => {
  const navigate = useNavigate();

  // Get logged-in user from localStorage
  const user = JSON.parse(localStorage.getItem("user"));
  const profilePicPath = user?.profilePicture
    ? `http://localhost:5001/${user.profilePicture}` // full path to backend static image
    : "https://via.placeholder.com/50"; // fallback image

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="navbar-wrapper position-absolute w-100">
      <div className="navbar-container d-flex align-items-center justify-content-between">

        {/* ðŸ”¹ Glass Bar â€“ Logo */}
        <div className="glass-navbar brand-glass d-flex align-items-center">
          <img
            src={logo}
            alt="Climatrix Logo"
            height="52"
          />
          <span className="brand-gradient fw-bold fs-4 ms-2">
            CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
          </span>
        </div>

        {/* ðŸ”¹ Glass Bar â€“ Profile Container */}
        <div className="glass-navbar nav-glass d-flex align-items-center gap-3 px-3 py-1">
          {/* Profile Picture */}
          <img
            src={profilePicPath}
            alt={user?.name || "Profile"}
            className="rounded-circle"
            style={{ width: "50px", height: "50px", objectFit: "cover", border: "2px solid #00FF41" }}
          />

          {/* Name & Email */}
          <div className="d-flex flex-column">
            <span className="fw-bold text-white">{user?.name}</span>
            <span className="text-white-50" style={{ fontSize: "0.85rem" }}>{user?.email}</span>
          </div>

          {/* Logout Button */}
          <button
            className="btn btn-danger d-flex align-items-center gap-1"
            onClick={handleLogout}
          >
            <FaSignOutAlt /> Logout
          </button>
        </div>

      </div>
    </div>
  );
};

export default ClientNavbar;
