import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ResetPassword = () => {
  const navigate = useNavigate();
  const { state } = useLocation();

  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");

  // Redirect if accessed directly without proper state
  useEffect(() => {
    if (!state?.email || !state?.code) {
      navigate("/forgot-password");
    }
  }, [state, navigate]);

  const submit = async () => {
    if (password !== confirm) return alert("Passwords do not match");

    await API.post("/auth/reset-password", {
      email: state.email,
      code: state.code,
      newPassword: password,
    });

    navigate("/login");
  };

  return (
    <div className="app-background login-background">
      <div className="auth-page">
        <div className="auth-glass-card">
          <button
            className="btn btn-link back-button"
            onClick={() => navigate(-1)}
          >
            &#8592;
          </button>

          <h3 className="text-center brand-gradient mb-4">Reset Password</h3>

          <input
            type="password"
            className="form-control glass-input mb-3"
            placeholder="New Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <input
            type="password"
            className="form-control glass-input mb-3"
            placeholder="Confirm Password"
            value={confirm}
            onChange={(e) => setConfirm(e.target.value)}
          />

          <button
            className="btn btn-green-custom w-100 py-2"
            onClick={submit}
          >
            Reset Password
          </button>
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
