import { useEffect, useState } from "react";
import bg1 from "../assets/5.jpg";
import logo from "../assets/logo.png";
import Footer from "../components/Footer";
import API from "../services/api";

const About = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [alert, setAlert] = useState({ type: "", message: "" });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (alert.message) {
      const timer = setTimeout(
        () => setAlert({ type: "", message: "" }),
        5000
      );
      return () => clearTimeout(timer);
    }
  }, [alert]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setAlert({ type: "", message: "" });

    if (!form.name.trim()) {
      setAlert({ type: "error", message: "Name is required" });
      return;
    }
    if (!form.email.includes("@")) {
      setAlert({ type: "error", message: "Invalid email address" });
      return;
    }
    if (form.message.length < 10) {
      setAlert({
        type: "error",
        message: "Query must be at least 10 characters",
      });
      return;
    }

    setLoading(true);
    try {
      await API.post("/contact/query", form);
      setAlert({
        type: "success",
        message: "Your query has been submitted successfully!",
      });
      setForm({ name: "", email: "", message: "" });
    } catch (err) {
      setAlert({
        type: "error",
        message:
          err.response?.data?.message ||
          "Failed to submit query. Try again.",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
  <div className="about-root">

    {/* FIXED BACKGROUND */}
    <div
      className="about-fixed-bg"
      style={{ backgroundImage: `url(${bg1})` }}
    >
      <div className="bg-overlay"></div>
    </div>

    {/* SCROLLABLE CONTENT */}
    <div className="about-page">

      {/* Alert */}
      {alert.message && (
        <div
          className={`login-alert ${
            alert.type === "error"
              ? "login-alert-error"
              : "login-alert-success"
          }`}
        >
          {alert.message}
        </div>
      )}

      {/* Loading */}
      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Loading..." className="spinner-logo-large" />
        </div>
      )}

      {!loading && (
        <div className="about-scroll-wrapper">

          {/* ABOUT HEADER */}
          <div className="about-header-glass">
            <h1 className="about-title">
              About <span className="brand-gradient">Climatrix</span>
            </h1>
            <p className="about-intro">
              Climatrix is an AI-driven climate intelligence platform designed
              to enhance rainfall prediction accuracy and disaster preparedness
              across Sri Lanka.
            </p>
          </div>

          {/* INFO CARDS */}
          <div className="about-info-grid">
            <div className="about-info-card">
              <h5><span className="brand-gradient">Our Mission</span></h5>
              <p>
                Deliver timely climate insights that support disaster risk
                reduction and community resilience.
              </p>
            </div>

            <div className="about-info-card">
              <h5>Technology</h5>
              <p>
                Advanced machine learning models trained on historical and
                real-time weather datasets.
              </p>
            </div>

            <div className="about-info-card">
              <h5><span className="brand-gradient">Impact</span></h5>
              <p>
                Supporting planners, researchers, and emergency response teams
                across Sri Lanka.
              </p>
            </div>
          </div>

          {/* QUERY FORM */}
          <div className="auth-glass-card about-query-card">
            <h4 className="text-center brand-gradient mb-4">
              Contact Us
            </h4>

            <form onSubmit={handleSubmit} autoComplete="off">
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control glass-input"
                  placeholder="Your Name"
                  value={form.name}
                  onChange={(e) =>
                    setForm({ ...form, name: e.target.value })
                  }
                />
              </div>

              <div className="mb-3">
                <input
                  type="email"
                  className="form-control glass-input"
                  placeholder="Your Email"
                  value={form.email}
                  onChange={(e) =>
                    setForm({ ...form, email: e.target.value })
                  }
                />
              </div>

              <div className="mb-4">
                <textarea
                  className="form-control glass-input"
                  placeholder="Your Query"
                  rows="4"
                  value={form.message}
                  onChange={(e) =>
                    setForm({ ...form, message: e.target.value })
                  }
                />
              </div>

              <button
                className="btn btn-green-custom w-100 py-2"
                type="submit"
              >
                Submit Query
              </button>
            </form>
          </div>

          <Footer />
        </div>
      )}
    </div>
  </div>
);

};

export default About;
