import { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";

const Register = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    role: "Client"
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await API.post("/auth/register", form);
    navigate("/login");
  };

  return (
    <div className="container mt-5" style={{ maxWidth: "450px" }}>
      <div className="card card-dark p-4">
        <h3 className="text-center brand-gradient mb-3">Register</h3>

        <form onSubmit={handleSubmit}>
          <input className="form-control mb-3" placeholder="Name" required
            onChange={(e) => setForm({ ...form, name: e.target.value })}
          />
          <input className="form-control mb-3" placeholder="Email" type="email" required
            onChange={(e) => setForm({ ...form, email: e.target.value })}
          />
          <input className="form-control mb-3" placeholder="Password" type="password" required
            onChange={(e) => setForm({ ...form, password: e.target.value })}
          />

          <button className="btn btn-green-custom w-100">Register</button>
        </form>

        <button className="btn btn-link text-light mt-2" onClick={() => navigate(-1)}>
          ‚Üê Back
        </button>
      </div>
    </div>
  );
};

export default Register;
