import { useEffect, useState } from "react";
import { FaCloudRain, FaExclamationTriangle, FaMapMarkedAlt, FaWater } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import bg1 from "../assets/1.jpg";
import bg2 from "../assets/2.jpg";
import bg3 from "../assets/3.jpg";
import bg4 from "../assets/4.jpg";

const backgrounds = [bg1, bg2, bg3, bg4];

const Home = () => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);

  // Change background every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % backgrounds.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="app-background animated-bg"
      style={{ backgroundImage: `url(${backgrounds[index]})` }}
    >
      <div className="bg-overlay"></div>

      <div className="home-page">
        <div className="home-glass-card text-center">
          {/* Title */}
          <h1 className="brand-gradient fw-bold mb-3">
            Climate Intelligence Platform
          </h1>

          {/* Subtitle */}
          <p className="home-subtext mb-4">
            AI-based Rainfall & Disaster Prediction for Sri Lanka
          </p>

          {/* Features (icons + labels) */}
          <div className="d-flex justify-content-around mb-4 mt-2">
            <div className="home-feature text-center">
              <FaCloudRain size={28} className="text-green" />
              <p className="feature-label mt-1">Rainfall Prediction</p>
            </div>
            <div className="home-feature text-center">
              <FaWater size={28} className="text-green" />
              <p className="feature-label mt-1">Flood Alerts</p>
            </div>
            <div className="home-feature text-center">
              <FaExclamationTriangle size={28} className="text-green" />
              <p className="feature-label mt-1">Disaster Warnings</p>
            </div>
            <div className="home-feature text-center">
              <FaMapMarkedAlt size={28} className="text-green" />
              <p className="feature-label mt-1">Interactive Maps</p>
            </div>
          </div>

          {/* Get Started Button */}
          <button
            className="btn btn-green-custom px-4 py-2"
            onClick={() => navigate("/login")}
          >
            Get Started
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
