import { useEffect, useState } from "react";
import { FaChartLine, FaCloudRain, FaExclamationTriangle, FaRobot } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import bg1 from "../assets/1.jpg";
import bg2 from "../assets/2.jpg";
import bg3 from "../assets/3.jpg";
import bg4 from "../assets/4.jpg";

const backgrounds = [bg1, bg2, bg3, bg4];

const Home = () => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % backgrounds.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="app-background animated-bg"
      style={{ backgroundImage: `url(${backgrounds[index]})` }}
    >
      <div className="bg-overlay"></div>

      <div className="home-page home-page-layout">
        {/* =====================
            1. MAIN CONTENT
        ===================== */}
        <div className="home-main-content text-center">
          <h1 className="home-title brand-gradient">
            Predict, Prepare, Protect
          </h1>
          <p className="home-subtext">
            AI-driven rainfall and disaster predictions for a safer Sri Lanka
          </p>
        </div>

        {/* =====================
            2. FEATURE CARDS
        ===================== */}
        <div className="home-card-grid">
          <div className="home-feature-card">
            <FaCloudRain size={32} />
            <h5 className="card-title">Rainfall Prediction</h5>
            <p className="card-text">Real-time rainfall forecasts powered by AI models.</p>
          </div>

          <div className="home-feature-card">
            <FaExclamationTriangle size={32} />
            <h5 className="card-title">Disaster Alerts</h5>
            <p className="card-text">Instant warnings for floods, landslides, and other events.</p>
          </div>

          <div className="home-feature-card">
            <FaChartLine size={32} />
            <h5 className="card-title">Analytics</h5>
            <p className="card-text">Analyze climate data trends for better planning.</p>
          </div>

          <div className="home-feature-card">
            <FaRobot size={32} />
            <h5 className="card-title">AI Models</h5>
            <p className="card-text">Advanced algorithms to enhance prediction accuracy.</p>
          </div>
        </div>

        {/* =====================
            3. ACTION BUTTONS
        ===================== */}
        <div className="home-action-buttons">
          <button className="btn btn-green-custom" onClick={() => navigate("/predictions")}>
            Predictions
          </button>
          <button className="btn btn-green-custom" onClick={() => navigate("/login")}>
            Login
          </button>
          <button className="btn btn-green-custom" onClick={() => navigate("/register")}>
            Register
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
