import { useNavigate } from "react-router-dom";
import logo from "../../assets/logo.png";

const ClientNavbar = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const profileUrl = user?.profilePicture
    ? `http://localhost:5001/${user.profilePicture}` // full backend URL
    : "/defaultProfile.jpg"; // fallback

  return (
    <div className="navbar-wrapper position-absolute w-100">
      <div className="navbar-container d-flex align-items-center justify-content-between">

        {/* Logo */}
        <div className="glass-navbar brand-glass d-flex align-items-center">
          <img src={logo} alt="Climatrix Logo" height={52} />
          <span className="brand-gradient fw-bold fs-4 ms-2">
            CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
          </span>
        </div>

        {/* Profile */}
        <div className="glass-navbar nav-glass d-flex align-items-center gap-3">
          <img
            src={profileUrl}
            alt="Profile"
            className="profile-picture rounded-circle"
            height={40}
            width={40}
          />
          <span className="fw-bold">{user?.name}</span>
          <button className="btn btn-danger btn-sm" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default ClientNavbar;
