import { BrowserRouter, Route, Routes, useLocation } from "react-router-dom";
import Footer from "./components/Footer";
import Navbar from "./components/Navbar";

// Pages
import About from "./pages/About";
import ForgetPassword from "./pages/ForgetPassword";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import ResetPassword from "./pages/ResetPassword";
import VerifyCode from "./pages/VerifyCode";

// Dashboards
import AdminDashboard from "./pages/admin/AdminDashboard";
import ClientDashboard from "./pages/client/ClientDashboard";
import PredictionHistory from "./pages/client/PredictionHistory";
import ClientPredictions from "./pages/client/Predictions";

// ✅ NEW Pages (you said these should also have VerticalBar)
import ClientInsights from "./pages/client/Insights";   // create if not yet
import ClientProfile from "./pages/client/Profile";     // create if not yet

// ✅ Vertical navigation bar
import VerticalBar from "./pages/client/VerticalBar";

import ProtectedRoute from "./routes/ProtectedRoute";

import "bootstrap/dist/css/bootstrap.min.css";
import "./styles/theme.css";

/* ✅ Layout for client pages WITHOUT sidebar */
const ClientLayout = ({ children }) => {
  return (
    <div className="d-flex vh-100">
      <div className="flex-grow-1 overflow-auto p-3">{children}</div>
    </div>
  );
};

/* ✅ Layout for ONLY selected pages WITH VerticalBar */
const ClientLayoutWithVerticalBar = ({ children }) => {
  return (
    <div className="d-flex vh-100">
      <VerticalBar />
      <div className="flex-grow-1 overflow-auto p-3 client-layout-with-sidebar">
        {children}
      </div>
    </div>
  );
};

function AppLayout() {
  const location = useLocation();
  const hideFooterOnHome = location.pathname === "/";

  return (
    <>
      {/* Only show top Navbar on public pages */}
      {!location.pathname.startsWith("/client") && <Navbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<ForgetPassword />} />
        <Route path="/verify-code" element={<VerifyCode />} />
        <Route path="/reset-password" element={<ResetPassword />} />
        <Route path="/about" element={<About />} />

        {/* Protected Admin Route */}
        <Route
          path="/admin/dashboard"
          element={
            <ProtectedRoute allowedRoles={["Admin"]}>
              <AdminDashboard />
            </ProtectedRoute>
          }
        />

        {/* ✅ Client Dashboard (NO VerticalBar) */}
        <Route
          path="/client/dashboard"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayout>
                <ClientDashboard />
              </ClientLayout>
            </ProtectedRoute>
          }
        />

        {/* ✅ Pages WITH VerticalBar ONLY */}
        <Route
          path="/client/predictions"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <ClientPredictions />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />

        <Route
          path="/client/prediction-history"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <PredictionHistory />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />

        <Route
          path="/client/insights"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <ClientInsights />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />

        <Route
          path="/client/profile"
          element={
            <ProtectedRoute allowedRoles={["Client"]}>
              <ClientLayoutWithVerticalBar>
                <ClientProfile />
              </ClientLayoutWithVerticalBar>
            </ProtectedRoute>
          }
        />
      </Routes>

      {!hideFooterOnHome && !location.pathname.startsWith("/client") && <Footer />}
    </>
  );
}

function App() {
  return (
    <BrowserRouter>
      <AppLayout />
    </BrowserRouter>
  );
}

export default App;
