import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import API from "../services/api";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await API.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      navigate("/");
    } catch (err) {
      setError(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="app-background">
      <div className="auth-page">
        <div className="auth-glass-card">

          <h3 className="text-center brand-gradient mb-4">
            Welcome Back
          </h3>

          {/* Glass Alert */}
          {error && (
            <div className="glass-alert glass-alert-danger mb-3">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
              <input
                type="email"
                className="form-control glass-input"
                placeholder="Email address"
                required
                onChange={(e) =>
                  setForm({ ...form, email: e.target.value })
                }
              />
            </div>

            <div className="form-group mb-4">
              <input
                type="password"
                className="form-control glass-input"
                placeholder="Password"
                required
                onChange={(e) =>
                  setForm({ ...form, password: e.target.value })
                }
              />
            </div>

            <button className="btn btn-green-custom w-100 py-2">
              Login
            </button>
          </form>

          <div className="text-center mt-4">
            <Link className="link-green" to="/forgot-password">
              Forgot password?
            </Link>
          </div>

          <div className="text-center mt-3">
            <button
              className="btn btn-link link-light"
              onClick={() => navigate(-1)}
            >
              ‚Üê Back
            </button>
          </div>

        </div>
      </div>
    </div>
  );
};

export default Login;
