// pages/admin/UserManagement.js
import axios from "axios";
import { useEffect, useMemo, useState } from "react";
import {
    FaPlus,
    FaRegEdit,
    FaRegTrashAlt,
    FaSyncAlt,
    FaTimes,
} from "react-icons/fa";

const API_BASE = "http://localhost:5001";
const API_PREFIX = "/api";
const API = axios.create({ baseURL: API_BASE });

const DEFAULT_PROFILE = "image/defaultProfile.jpg";

const toDateTime = (iso) => {
  if (!iso) return "—";
  const d = new Date(iso);
  if (Number.isNaN(d.getTime())) return "—";
  return d.toLocaleString();
};

const UserManagement = () => {
  const stored = useMemo(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  }, []);

  const adminId = stored?.userId;

  // data
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);

  // UI state
  const [query, setQuery] = useState("");
  const [roleFilter, setRoleFilter] = useState("All");
  const [msg, setMsg] = useState({ type: "", text: "" });

  // modal state (add/edit)
  const [showModal, setShowModal] = useState(false);
  const [mode, setMode] = useState("add"); // add | edit
  const [selected, setSelected] = useState(null);

  // delete confirm
  const [deleteTarget, setDeleteTarget] = useState(null);

  // form state
  const [form, setForm] = useState({
    name: "",
    email: "",
    role: "Client",
    password: "", // ONLY used in Add
  });

  const showToast = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: "", text: "" }), 2600);
  };

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const res = await API.get(`${API_PREFIX}/users`);
      // Your backend returns array of users (with profilePicture as URL)
      setUsers(Array.isArray(res.data) ? res.data : []);
    } catch (e) {
      showToast("error", "Failed to load users");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [adminId]);

  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();

    return users
      .filter((u) => {
        if (roleFilter === "All") return true;
        return String(u.role || "").toLowerCase() === roleFilter.toLowerCase();
      })
      .filter((u) => {
        if (!q) return true;
        const hay = `${u.userId} ${u.name} ${u.email} ${u.role}`.toLowerCase();
        return hay.includes(q);
      });
  }, [users, query, roleFilter]);

  // =========================
  // Modal helpers
  // =========================
  const openAdd = () => {
    setMode("add");
    setSelected(null);
    setForm({ name: "", email: "", role: "Client", password: "" });
    setShowModal(true);
  };

  const openEdit = (u) => {
    setMode("edit");
    setSelected(u);
    setForm({
      name: u?.name || "",
      email: u?.email || "",
      role: u?.role || "Client",
      password: "", // not used in edit
    });
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelected(null);
    setMode("add");
    setForm({ name: "", email: "", role: "Client", password: "" });
  };

  // =========================
  // CRUD actions
  // =========================
  const submitAdd = async () => {
    const name = form.name.trim();
    const email = form.email.trim();
    const password = form.password;

    if (!name || !email || !password) {
      showToast("error", "Name, Email, and Password are required");
      return;
    }

    try {
      await API.post(`${API_PREFIX}/user`, {
        name,
        email,
        password,
        role: form.role,
        // ✅ force default path as requested (backend also defaults)
        profilePicture: DEFAULT_PROFILE,
      });

      showToast("success", "User added successfully");
      closeModal();
      fetchUsers();
    } catch (e) {
      showToast("error", e?.response?.data?.message || "Failed to add user");
    }
  };

  const submitEdit = async () => {
    if (!selected?.userId) return;

    const name = form.name.trim();
    const email = form.email.trim();

    if (!name || !email) {
      showToast("error", "Name and Email are required");
      return;
    }

    try {

      await API.put(`${API_PREFIX}/user/${selected.userId}`, {
        name,
        email,
        role: form.role,
      });

      showToast("success", "User updated successfully");
      closeModal();
      fetchUsers();
    } catch (e) {
      showToast("error", e?.response?.data?.message || "Failed to update user");
    }
  };

  const confirmDelete = (u) => {
    setDeleteTarget(u);
  };

  const cancelDelete = () => {
    setDeleteTarget(null);
  };

  const doDelete = async () => {
    if (!deleteTarget?.userId) return;

    try {
      await API.delete(`${API_PREFIX}/user/${deleteTarget.userId}`);
      showToast("success", "User deleted successfully");
      setDeleteTarget(null);
      fetchUsers();
    } catch (e) {
      showToast("error", e?.response?.data?.message || "Failed to delete user");
    }
  };

  return (
    <div className="history-page">
      <div className="history-container">
        {/* HERO */}
        <div className="history-hero">
          <h1 className="history-title">
            <span className="brand-gradient">User</span> Management
          </h1>
          <p className="history-subtitle">
            View, add, update, and delete system users (Admin/Client)
          </p>

          {msg.text && (
            <div
              className={`mt-3 chip chip-strong`}
              style={{
                display: "inline-flex",
                gap: 10,
                alignItems: "center",
                borderColor:
                  msg.type === "success"
                    ? "rgba(60,255,140,0.45)"
                    : "rgba(255,60,60,0.5)",
                background:
                  msg.type === "success"
                    ? "rgba(60,255,140,0.16)"
                    : "rgba(255,60,60,0.16)",
              }}
            >
              {msg.text}
            </div>
          )}
        </div>

        {/* CONTROLS */}
        <div className="glass-panel history-controls">
          <div className="history-controls-row">
            <input
              className="form-control glass-input history-search"
              placeholder="Search by name, email, role, or userId..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />

            <select
              className="form-select glass-select history-select"
              value={roleFilter}
              onChange={(e) => setRoleFilter(e.target.value)}
            >
              <option value="All">All Roles</option>
              <option value="Admin">Admin</option>
              <option value="Client">Client</option>
            </select>

            <button
              className="btn btn-green-custom history-refresh"
              onClick={openAdd}
            >
              <FaPlus className="me-2" />
              Add User
            </button>

            <button
              className="btn btn-outline-light history-refresh icon-btn"
              onClick={fetchUsers}
              disabled={loading}
              title="Refresh"
            >
              <FaSyncAlt />
            </button>
          </div>

          <div className="history-count text-white-50">
            Showing <b>{filtered.length}</b> of <b>{users.length}</b> users
          </div>
        </div>

        {/* TABLE */}
        <div className="glass-panel">
          <div className="ins-table-wrap">
            <table className="table table-dark table-hover mb-0 ins-table">
              <thead>
                <tr>
                  <th>User ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Verified</th>
                  <th>Alerts</th>
                  <th>Created</th>
                  <th style={{ width: 130 }}>Actions</th>
                </tr>
              </thead>

              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan="8" className="text-center text-white-50 py-4">
                      Loading...
                    </td>
                  </tr>
                ) : filtered.length === 0 ? (
                  <tr>
                    <td colSpan="8" className="text-center text-white-50 py-4">
                      No users found
                    </td>
                  </tr>
                ) : (
                  filtered.map((u) => (
                    <tr key={u.userId}>
                      <td>{u.userId}</td>
                      <td>{u.name}</td>
                      <td>{u.email}</td>
                      <td>
                        <span className="chip">
                          {u.role === "Admin" ? "Admin" : "Client"}
                        </span>
                      </td>
                      <td>{u.isVerified ? "Yes" : "No"}</td>
                      <td>{u.emailAlertsEnabled ? "Enabled" : "Disabled"}</td>
                      <td>{toDateTime(u.createdAt)}</td>
                      <td>
                        <div style={{ display: "flex", gap: 10 }}>
                          <button
                            className="icon-action-btn"
                            title="Edit"
                            onClick={() => openEdit(u)}
                          >
                            <FaRegEdit />
                          </button>

                          <button
                            className="icon-action-btn"
                            title="Delete"
                            onClick={() => confirmDelete(u)}
                            disabled={u.userId === adminId} // prevent self-delete (optional safety)
                          >
                            <FaRegTrashAlt />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          <div className="ins-table-note text-white-50">
            Admin cannot edit <b>password</b> or <b>profile picture</b> from this page.
          </div>
        </div>
      </div>

      {/* =======================
          ADD / EDIT MODAL
      ======================= */}
      {showModal && (
        <div className="history-modal-overlay" onClick={closeModal}>
          <div
            className="history-modal-glass"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="history-modal-header">
              <div className="history-modal-title">
                {mode === "add" ? "Add New User" : `Update User #${selected?.userId}`}
              </div>

              <button className="history-modal-close" onClick={closeModal}>
                <FaTimes />
              </button>
            </div>

            <div className="history-modal-body">
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="pred-label">Name</label>
                  <input
                    className="form-control glass-input"
                    value={form.name}
                    onChange={(e) => setForm((p) => ({ ...p, name: e.target.value }))}
                    placeholder="Enter name"
                  />
                </div>

                <div className="col-md-6">
                  <label className="pred-label">Email</label>
                  <input
                    className="form-control glass-input"
                    value={form.email}
                    onChange={(e) => setForm((p) => ({ ...p, email: e.target.value }))}
                    placeholder="Enter email"
                  />
                </div>

                <div className="col-md-6">
                  <label className="pred-label">Role</label>
                  <select
                    className="form-select glass-select"
                    value={form.role}
                    onChange={(e) => setForm((p) => ({ ...p, role: e.target.value }))}
                  >
                    <option value="Client">Client</option>
                    <option value="Admin">Admin</option>
                  </select>
                </div>

                {/* ✅ Password ONLY for Add */}
                {mode === "add" && (
                  <div className="col-md-6">
                    <label className="pred-label">Password</label>
                    <input
                      type="password"
                      className="form-control glass-input"
                      value={form.password}
                      onChange={(e) =>
                        setForm((p) => ({ ...p, password: e.target.value }))
                      }
                      placeholder="Set temporary password"
                    />
                    <div className="text-white-50 mt-2" style={{ fontSize: 12 }}>
                      Default profile picture will be saved automatically.
                    </div>
                  </div>
                )}
              </div>

              <div className="profile-actions" style={{ marginTop: 18 }}>
                <button className="btn btn-outline-light profile-btn" onClick={closeModal}>
                  Cancel
                </button>

                <button
                  className="btn btn-green-custom profile-btn"
                  onClick={mode === "add" ? submitAdd : submitEdit}
                >
                  {mode === "add" ? "Add User" : "Update User"}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* =======================
          DELETE CONFIRM MODAL
      ======================= */}
      {deleteTarget && (
        <div className="history-modal-overlay" onClick={cancelDelete}>
          <div
            className="history-modal-glass"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="history-modal-header">
              <div className="history-modal-title">Confirm Delete</div>

              <button className="history-modal-close" onClick={cancelDelete}>
                <FaTimes />
              </button>
            </div>

            <div className="history-modal-body">
              <p className="text-white" style={{ marginBottom: 8 }}>
                Are you sure you want to delete this user?
              </p>

              <div className="text-white-50" style={{ fontSize: 13 }}>
                <div>
                  <b>User:</b> #{deleteTarget.userId} — {deleteTarget.name}
                </div>
                <div>
                  <b>Email:</b> {deleteTarget.email}
                </div>
                <div className="mt-2">
                  This action cannot be undone.
                </div>
              </div>

              <div className="profile-actions" style={{ marginTop: 18 }}>
                <button className="btn btn-outline-light profile-btn" onClick={cancelDelete}>
                  Cancel
                </button>

                <button className="btn btn-danger profile-btn" onClick={doDelete}>
                  Delete User
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserManagement;
