// pages/client/Profile.js
import axios from "axios";
import { useEffect, useMemo, useRef, useState } from "react";
import { FaCamera, FaLock, FaSave, FaTimes, FaUser } from "react-icons/fa";
import logo from "../../assets/logo.png";
import Footer from "../../components/Footer";

const API_BASE = "http://localhost:5001"; // backend host
const API_PREFIX = "/api"; // your routes are under /api

const API = axios.create({
  baseURL: API_BASE,
});

const formatDateTime = (iso) => {
  if (!iso) return "—";
  const d = new Date(iso);
  if (Number.isNaN(d.getTime())) return "—";
  return d.toLocaleString();
};

// ✅ Works with:
// - "http://localhost:5001/image/a.jpg"
// - "/image/a.jpg"
// - "image/a.jpg"
// ✅ Also repairs broken url like: http://localhost:5001/undefined//image/xxx.png
const normalizeImageUrl = (pic) => {
  if (!pic) return "";

  let s = String(pic).trim();

  // Fix common broken formats coming from backend
  s = s.replace("/undefined/", "/");
  s = s.replace("undefined//", "");
  s = s.replace(/\/{2,}/g, "/"); // collapse double slashes (keeps http:/ issue below)

  // If it was a full URL, restore "http://" after collapsing slashes
  if (/^https?:\/\//i.test(String(pic).trim())) {
    // If we accidentally collapsed to "http:/", fix it:
    s = s.replace(/^http:\//i, "http://").replace(/^https:\//i, "https://");
    return s;
  }

  // Otherwise treat it as a relative path
  const clean = s.replace(/^\/+/, "");
  return `${API_BASE}/${clean}`;
};

// ✅ Prevent localStorage corruption (never store undefined/null url)
const isValidProfilePicture = (url) => {
  if (!url) return false;
  const s = String(url);
  if (s.includes("undefined") || s.includes("null")) return false;
  return true;
};

const Profile = () => {
  // ✅ get logged user from localStorage
  const stored = useMemo(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  }, []);

  const userId = stored?.userId;

  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ type: "", message: "" });

  const [profile, setProfile] = useState(null);

  const [name, setName] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState("");

  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const fileRef = useRef(null);

  // Auto-hide alert
  useEffect(() => {
    if (!alert.message) return;
    const t = setTimeout(() => setAlert({ type: "", message: "" }), 3000);
    return () => clearTimeout(t);
  }, [alert]);

  const updateLocalStorageUser = (updates) => {
    try {
      const existing = JSON.parse(localStorage.getItem("user")) || {};
      const merged = { ...existing, ...updates };
      localStorage.setItem("user", JSON.stringify(merged));
      window.dispatchEvent(new Event("userUpdated")); // ✅ navbar refresh
    } catch {}
  };

  // ✅ Fetch profile by userId from DB
  const fetchProfile = async () => {
    if (!userId) {
      setAlert({ type: "error", message: "User not logged in" });
      return;
    }

    setLoading(true);
    try {
      // ✅ GET /api/user/:userId
      const res = await API.get(`${API_PREFIX}/user/${userId}`);
      const u = res.data;

      const fixedPic = normalizeImageUrl(u?.profilePicture);

      setProfile({
        ...u,
        profilePicture: fixedPic,
      });

      setName(u?.name || "");

      // ✅ only use preview if valid
      setPreviewUrl(isValidProfilePicture(fixedPic) ? fixedPic : "");

      // ✅ sync localStorage for navbar (DO NOT corrupt profilePicture)
      updateLocalStorageUser({
        userId: u?.userId,
        name: u?.name,
        email: u?.email,
        role: u?.role,
        ...(isValidProfilePicture(fixedPic) ? { profilePicture: fixedPic } : {}),
      });
    } catch (e) {
      console.error(e);
      setAlert({
        type: "error",
        message: e?.response?.data?.message || "Failed to load profile",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  // ✅ image pick
  const onPickImage = (file) => {
    if (!file) return;

    const okTypes = ["image/jpeg", "image/png", "image/webp", "image/jpg"];
    if (!okTypes.includes(file.type)) {
      setAlert({ type: "error", message: "Upload JPG/PNG/WEBP only" });
      return;
    }

    if (file.size > 3 * 1024 * 1024) {
      setAlert({ type: "error", message: "Image must be under 3MB" });
      return;
    }

    setImageFile(file);

    // local preview
    const url = URL.createObjectURL(file);
    setPreviewUrl(url);
  };

  const resetChanges = () => {
    if (!profile) return;

    setName(profile.name || "");
    setImageFile(null);
    setPreviewUrl(profile.profilePicture || "");
    setCurrentPassword("");
    setNewPassword("");
    setConfirmPassword("");
    setAlert({ type: "", message: "" });
  };

  // ✅ Save profile changes
  const handleSave = async (e) => {
    e.preventDefault();

    if (!userId) {
      setAlert({ type: "error", message: "User not logged in" });
      return;
    }

    if (!name.trim()) {
      setAlert({ type: "error", message: "Name is required" });
      return;
    }

    const wantsPasswordChange =
      currentPassword || newPassword || confirmPassword;

    if (wantsPasswordChange) {
      if (!currentPassword || !newPassword || !confirmPassword) {
        setAlert({ type: "error", message: "Fill all password fields" });
        return;
      }
      if (newPassword.length < 6) {
        setAlert({
          type: "error",
          message: "New password must be 6+ characters",
        });
        return;
      }
      if (newPassword !== confirmPassword) {
        setAlert({ type: "error", message: "Confirm password does not match" });
        return;
      }
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("name", name.trim());

      // ✅ Email is read-only → do NOT send email
      if (wantsPasswordChange) {
        formData.append("currentPassword", currentPassword);
        formData.append("newPassword", newPassword);
        formData.append("confirmPassword", confirmPassword);
      }

      if (imageFile) formData.append("image", imageFile);

      // ✅ PUT /api/user/:userId/profile
      const res = await API.put(
        `${API_PREFIX}/user/${userId}/profile`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      const updated = res.data?.user;
      const fixedPic = normalizeImageUrl(updated?.profilePicture);

      setAlert({ type: "success", message: "Profile updated successfully" });

      // ✅ Update UI
      setProfile((prev) => ({
        ...(prev || {}),
        name: updated?.name ?? name.trim(),
        email: updated?.email ?? prev?.email,
        role: updated?.role ?? prev?.role,
        profilePicture: isValidProfilePicture(fixedPic)
          ? fixedPic
          : prev?.profilePicture,
      }));

      if (isValidProfilePicture(fixedPic)) setPreviewUrl(fixedPic);

      // ✅ Update localStorage safely (no corruption)
      const safePic = isValidProfilePicture(fixedPic)
        ? fixedPic
        : stored?.profilePicture;

      updateLocalStorageUser({
        name: updated?.name ?? name.trim(),
        ...(safePic ? { profilePicture: safePic } : {}),
      });

      // clear temp fields
      setImageFile(null);
      setCurrentPassword("");
      setNewPassword("");
      setConfirmPassword("");
    } catch (err) {
      console.error(err);
      const msg =
        err.response?.data?.message ||
        err.response?.data?.error ||
        "Profile update failed";
      setAlert({ type: "error", message: msg });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-page d-flex flex-column">
      {alert.message && (
        <div
          className={`login-alert ${
            alert.type === "error"
              ? "login-alert-error"
              : "login-alert-success"
          }`}
        >
          {alert.message}
        </div>
      )}

      {loading && (
        <div className="loading-overlay">
          <img src={logo} alt="Loading..." className="spinner-logo-large" />
        </div>
      )}

      <div className="profile-container flex-grow-1">
        <div className="profile-hero">
          <h1 className="profile-title">
            <span className="brand-gradient">My Profile</span>
          </h1>
          <div className="text-white-50">
            Update your name, profile picture, and password.
          </div>
        </div>

        <div className="profile-grid">
          {/* LEFT CARD */}
          <div className="profile-glass-card">
            <div className="profile-avatar-wrap">
              <div className="profile-avatar-ring">
                <img
                  src={previewUrl || "https://via.placeholder.com/160?text=User"}
                  alt="Profile"
                  className="profile-avatar"
                  onError={(e) => {
                    e.currentTarget.src =
                      "https://via.placeholder.com/160?text=User";
                  }}
                />
              </div>

              <button
                type="button"
                className="profile-avatar-btn"
                onClick={() => fileRef.current?.click()}
                title="Change profile picture"
              >
                <FaCamera />
              </button>

              <input
                ref={fileRef}
                type="file"
                accept="image/*"
                style={{ display: "none" }}
                onChange={(e) => onPickImage(e.target.files?.[0])}
              />
            </div>

            <div className="profile-summary">
              <div className="profile-name">{profile?.name || "—"}</div>
              <div className="profile-role-chip">
                {profile?.role || "Client"}
              </div>

              <div className="profile-kv">
                <div className="profile-k">Email</div>
                <div className="profile-v">{profile?.email || "—"}</div>
              </div>

              <div className="profile-kv">
                <div className="profile-k">Joined</div>
                <div className="profile-v">
                  {formatDateTime(profile?.createdAt)}
                </div>
              </div>
            </div>
          </div>

          {/* RIGHT CARD */}
          <div className="profile-glass-card">
            <form onSubmit={handleSave}>
              <div className="profile-section-title">
                <FaUser className="me-2" /> Profile Details
              </div>

              <label className="profile-label">Name</label>
              <input
                className="form-control glass-input mb-3"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Your name"
                required
              />

              <label className="profile-label">Email</label>
              <input
                className="form-control glass-input mb-3"
                value={profile?.email || ""}
                disabled
              />

              <div className="profile-section-title mt-4">
                <FaLock className="me-2" /> Change Password
              </div>

              <div className="profile-password-grid">
                <div>
                  <label className="profile-label">Current Password</label>
                  <input
                    type="password"
                    className="form-control glass-input"
                    value={currentPassword}
                    onChange={(e) => setCurrentPassword(e.target.value)}
                    placeholder="Enter current password"
                  />
                </div>

                <div>
                  <label className="profile-label">New Password</label>
                  <input
                    type="password"
                    className="form-control glass-input"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    placeholder="Enter new password"
                  />
                </div>

                <div>
                  <label className="profile-label">Confirm Password</label>
                  <input
                    type="password"
                    className="form-control glass-input"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    placeholder="Confirm new password"
                  />
                </div>
              </div>

              <div className="profile-actions">
                <button
                  type="button"
                  className="btn btn-outline-light profile-btn"
                  onClick={resetChanges}
                >
                  <FaTimes className="me-2" />
                  Reset
                </button>

                <button
                  type="submit"
                  className="btn btn-green-custom profile-btn"
                >
                  <FaSave className="me-2" />
                  Save Changes
                </button>
              </div>

              <div className="profile-hint text-white-50">
                Tip: Keep password fields empty if you don’t want to change it.
              </div>
            </form>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Profile;
