import { useEffect, useState } from "react";
import bg1 from "../assets/1.jpg";
import bg2 from "../assets/2.jpg";
import bg3 from "../assets/3.jpg";
import bg4 from "../assets/4.jpg";
import Footer from "../components/Footer";

const backgrounds = [bg1, bg2, bg3, bg4];

const About = () => {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % backgrounds.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="app-background animated-bg about-scroll"
      style={{ backgroundImage: `url(${backgrounds[index]})` }}
    >
      <div className="bg-overlay"></div>

      <div className="home-simple">

        {/* ===== MAIN CONTENT ===== */}
        <div className="home-content">
          <h1 className="home-title">
            About <span className="brand-gradient">Climatrix</span>
          </h1>

          <p className="home-subtitle">
            A climate intelligence platform designed to support rainfall
            prediction and disaster preparedness in Sri Lanka.
          </p>

          <p className="home-description">
            Climatrix leverages machine learning and climate data analytics
            to identify rainfall patterns, flood risks, and extreme weather
            conditions before they occur. The system is designed to assist
            authorities, researchers, and communities in making timely,
            informed decisions.
          </p>

          <p className="home-description">
            By transforming complex climate data into actionable insights,
            Climatrix aims to strengthen disaster readiness, minimize impact,
            and promote climate resilience across the country.
          </p>
        </div>

        {/* ===== CONTACT GLASS CARD ===== */}
        <div className="about-contact-glass">
          <h4 className="brand-gradient mb-3">Contact Information</h4>

          <p>Email: <span>support@climatrix.lk</span></p>
          <p>Phone: <span>+94 77 123 4567</span></p>
          <p>Location: <span>Sri Lanka</span></p>
        </div>

        {/* ===== FOOTER ===== */}
        <Footer />

      </div>
    </div>
  );
};

export default About;
