import { useEffect, useMemo, useState } from "react";
import { FaSignOutAlt } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import logo from "../../assets/logo.png";

const API_HOST = "http://localhost:5001"; // backend host (for images)

const normalizeImageUrl = (pic) => {
  if (!pic) return "https://via.placeholder.com/50";

  // If backend already returns full URL (recommended): "http://localhost:5001/image/xxx.jpg"
  if (/^https?:\/\//i.test(pic)) return pic;

  // If stored as "/image/xxx.jpg" or "image/xxx.jpg"
  const clean = String(pic).replace(/^\/+/, "");
  return `${API_HOST}/${clean}`;
};

const ClientNavbar = () => {
  const navigate = useNavigate();
  const [showConfirm, setShowConfirm] = useState(false);

  // 笨 store user in state (reactive)
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  });

  // 笨 compute image safely
  const profilePicUrl = useMemo(() => {
    return normalizeImageUrl(user?.profilePicture);
  }, [user?.profilePicture]);

  // 笨 listen for profile updates (Profile page will dispatch this event)
  useEffect(() => {
    const refreshUser = () => {
      try {
        setUser(JSON.parse(localStorage.getItem("user")) || null);
      } catch {
        setUser(null);
      }
    };

    window.addEventListener("userUpdated", refreshUser);
    return () => window.removeEventListener("userUpdated", refreshUser);
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <>
      <div className="navbar-wrapper position-absolute w-100">
        <div className="navbar-container d-flex align-items-center justify-content-between">
          {/* 沐ｹ Logo Section */}
          <div className="glass-navbar brand-glass d-flex align-items-center">
            <img src={logo} alt="Climatrix Logo" height="52" />
            <span className="brand-gradient fw-bold fs-4 ms-2">
              CLIMATRIX <span style={{ color: "#00FF41" }}>SL</span>
            </span>
          </div>

          {/* 沐ｹ Profile Section */}
          <div className="glass-navbar nav-glass d-flex align-items-center gap-3 px-3 py-1">
            <img
              src={profilePicUrl}
              alt={user?.name || "Profile"}
              className="rounded-circle"
              style={{
                width: "50px",
                height: "50px",
                objectFit: "cover",
                border: "2px solid #00FF41",
              }}
              onError={(e) => {
                e.currentTarget.src = "https://via.placeholder.com/50";
              }}
            />

