import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo.png"; // logo for spinner
import API from "../services/api";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [alert, setAlert] = useState({ type: "", message: "" });
  const [loading, setLoading] = useState(false);

  // Auto-hide alert after 3 seconds
  useEffect(() => {
    if (alert.message) {
      const timer = setTimeout(() => setAlert({ type: "", message: "" }), 3000);
      return () => clearTimeout(timer);
    }
  }, [alert]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setAlert({ type: "", message: "" });
    setLoading(true);

    try {
      const res = await API.post("/auth/login", form);
      localStorage.setItem("token", res.data.token);
      setAlert({ type: "success", message: "Login successful!" });
      navigate("/");
    } catch (err) {
      setAlert({ type: "error", message: err.response?.data?.message || "Login failed" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-background login-background">
      {/* Alert message */}
      {alert.message && (
        <div
          className={`login-alert ${
            alert.type === "error" ? "login-alert-error" : "login-alert-success"
          }`}
        >
          {alert.message}
        </div>
      )}

      {/* Back button */}
      <button
        className="btn btn-link back-button"
        onClick={() => navigate(-1)}
      >
        &#8592;
      </button>

      <div className="auth-page">
        <div className="auth-glass-card">

          <h3 className="text-center brand-gradient mb-4">
            Welcome Back
          </h3>

          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <input
                type="email"
                className="form-control glass-input"
                placeholder="Email address"
                required
                onChange={(e) =>
                  setForm({ ...form, email: e.target.value })
                }
              />
            </div>

            <div className="mb-4">
              <input
                type="password"
                className="form-control glass-input"
                placeholder="Password"
                required
                onChange={(e) =>
                  setForm({ ...form, password: e.target.value })
                }
              />
            </div>

            <button
              className="btn btn-green-custom w-100 py-2"
              type="submit"
              disabled={loading}
            >
              {loading ? (
                <img src={logo} alt="Loading..." className="spinner-logo" />
              ) : (
                "Login"
              )}
            </button>
          </form>

          <div className="text-center mt-3 signup-text">
            Do not have an account? <Link className="link-green" to="/register">Sign Up</Link>
          </div>

          <div className="text-center mt-3">
            <Link className="link-green" to="/forgot-password">
              Forgot password?
            </Link>
          </div>

        </div>
      </div>
    </div>
  );
};

export default Login;
