import { useEffect, useState } from "react";
import { FaChartLine, FaCloudRain, FaExclamationTriangle, FaRobot } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import bg1 from "../assets/1.jpg";
import bg2 from "../assets/2.jpg";
import bg3 from "../assets/3.jpg";
import bg4 from "../assets/4.jpg";

const backgrounds = [bg1, bg2, bg3, bg4];

const Home = () => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % backgrounds.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div
      className="app-background animated-bg"
      style={{ backgroundImage: `url(${backgrounds[index]})` }}
    >
      <div className="bg-overlay"></div>

      <div className="home-page">
        <div className="home-glass-card text-center p-4">
          {/* Hero */}
          <h1 className="brand-gradient fw-bold mb-2">
            Climate Intelligence Platform
          </h1>
          <p className="home-subtext mb-4">
            AI-powered rainfall & disaster prediction system for Sri Lanka
          </p>

          {/* Features */}
          <div className="d-flex justify-content-around flex-wrap mb-4">
            <div className="home-feature">
              <FaCloudRain size={28} />
              <span className="feature-label">Rainfall Prediction</span>
            </div>
            <div className="home-feature">
              <FaExclamationTriangle size={28} />
              <span className="feature-label">Disaster Alerts</span>
            </div>
            <div className="home-feature">
              <FaChartLine size={28} />
              <span className="feature-label">Data Analytics</span>
            </div>
            <div className="home-feature">
              <FaRobot size={28} />
              <span className="feature-label">AI Forecast Models</span>
            </div>
          </div>

          {/* CTA */}
          <button
            className="btn btn-green-custom px-4 py-2"
            onClick={() => navigate("/login")}
          >
            Get Started
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
