import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const VerifyCode = () => {
  const navigate = useNavigate();
  const { state } = useLocation();
  const [code, setCode] = useState("");

  return (
    <div className="container mt-5" style={{ maxWidth: "400px" }}>
      <div className="card card-dark p-4">
        <h4 className="brand-gradient text-center mb-3">Verify Code</h4>

        <input className="form-control mb-3" placeholder="OTP Code"
          onChange={(e) => setCode(e.target.value)}
        />

        <button
          className="btn btn-green-custom w-100"
          onClick={() =>
            navigate("/reset-password", {
              state: { email: state.email, code }
            })
          }
        >
          Verify
        </button>

        <button className="btn btn-link text-light mt-2" onClick={() => navigate(-1)}>
          ‚Üê Back
        </button>
      </div>
    </div>
  );
};

export default VerifyCode;
